import React, { useEffect, useState, useMemo } from "react";
import API from "../../api/api";
import { FaUsers, FaBoxOpen, FaShoppingCart, FaTrash, FaMoon, FaSun, FaEdit } from "react-icons/fa";
import { LineChart, Line, XAxis, YAxis, Tooltip, CartesianGrid, ResponsiveContainer, BarChart, Bar, Legend } from "recharts";
import { format } from "date-fns";

export default function AdminPanel() {
  // app state
  const [dark, setDark] = useState(() => !!document.documentElement.classList.contains("dark"));
  const [products, setProducts] = useState([]);
  const [form, setForm] = useState({});
  const [file, setFile] = useState(null);
  const [editingId, setEditingId] = useState(null);
  const [activeTab, setActiveTab] = useState("products");
  const [loading, setLoading] = useState(false);

  // pagination + search
  const [search, setSearch] = useState("");
  const [page, setPage] = useState(1);
  const [pageSize, setPageSize] = useState(6);

  // fetch
  useEffect(() => {
    load();
  }, []);

  // toggle dark mode - updates <html> class for Tailwind
  useEffect(() => {
    if (dark) document.documentElement.classList.add("dark");
    else document.documentElement.classList.remove("dark");
  }, [dark]);

  async function load() {
    setLoading(true);
    try {
      const res = await API.get("/products");
      setProducts(res.data.products || res.data || []);
    } catch (err) {
      console.error(err);
      alert("Failed to load products");
    } finally {
      setLoading(false);
    }
  }

  // create or update
  async function submit(e) {
    e.preventDefault();
    const fd = new FormData();
    fd.append("name", form.name || "");
    fd.append("description", form.description || "");
    fd.append("price", form.price || 0);
    fd.append("colors", form.colors || "");
    fd.append("countInStock", form.countInStock || 0);
    if (file) fd.append("image", file);

    try {
      if (editingId) {
        await API.put(`/admin/product/${editingId}`, fd, {
          headers: { "Content-Type": "multipart/form-data" },
        });
        alert("Product updated");
      } else {
        await API.post("/admin/product", fd, {
          headers: { "Content-Type": "multipart/form-data" },
        });
        alert("Product added");
      }
      setForm({});
      setFile(null);
      setEditingId(null);
      load();
    } catch (err) {
      console.error(err);
      alert(err.response?.data?.message || err.message);
    }
  }

  // delete
  async function remove(id) {
    if (!window.confirm("Delete product permanently?")) return;
    try {
      await API.delete(`/admin/product/${id}`);
      load();
    } catch (e) {
      alert(e.message || "Delete failed");
    }
  }

  // edit: load into form
  function startEdit(p) {
    setEditingId(p._id);
    setForm({
      name: p.name,
      description: p.description,
      price: p.price,
      colors: p.colors,
      countInStock: p.countInStock,
    });
    window.scrollTo({ top: 0, behavior: "smooth" });
  }

  // Search + filtered products
  const filtered = useMemo(() => {
    const q = search.trim().toLowerCase();
    if (!q) return products;
    return products.filter((p) => (p.name || "").toLowerCase().includes(q));
  }, [products, search]);

  // pagination
  const totalPages = Math.max(1, Math.ceil(filtered.length / pageSize));
  useEffect(() => { if (page > totalPages) setPage(totalPages); }, [totalPages]); // clamp page

  const paginated = useMemo(() => {
    const start = (page - 1) * pageSize;
    return filtered.slice(start, start + pageSize);
  }, [filtered, page, pageSize]);

  // Sales chart data: ideally fetch from API; if not available, derive mock totals by product.price * random qty
  const chartData = useMemo(() => {
    // try to fetch sales endpoint: fallback to derived
    // Here we derive monthly totals for last 6 months
    const now = new Date();
    const months = Array.from({ length: 6 }).map((_, i) => {
      const d = new Date(now.getFullYear(), now.getMonth() - (5 - i), 1);
      return { month: format(d, "MMM yyyy"), sales: 0, orders: Math.floor(Math.random() * 10 + 5) };
    });

    // distribute some fake sales based on product prices and stock
    products.forEach((p, idx) => {
      const base = parseFloat(p.price || 0) || 0;
      const monthIndex = idx % months.length;
      months[monthIndex].sales += base * (1 + (p.countInStock ? Math.max(0, 5 - p.countInStock) : 2)) * (Math.random() * 2);
    });

    // normalize numbers
    return months.map(m => ({ ...m, sales: Math.round(m.sales) }));
  }, [products]);

  return (
    <div className="min-h-screen bg-gray-100 dark:bg-gray-900 text-gray-800 dark:text-gray-100 transition-colors">
      <div className="flex">
        {/* SIDEBAR */}
        <aside className="w-64 bg-white dark:bg-gray-800 p-6 border-r dark:border-gray-700">
          <div className="flex items-center justify-between mb-6">
            <h1 className="text-xl font-bold">Admin</h1>
            <button
              title="Toggle dark"
              onClick={() => setDark(d => !d)}
              className="p-2 rounded hover:bg-gray-200 dark:hover:bg-gray-700"
            >
              {dark ? <FaSun /> : <FaMoon />}
            </button>
          </div>

          <nav className="space-y-2">
            <button
              onClick={() => setActiveTab("products")}
              className={`flex items-center gap-3 w-full p-3 rounded ${activeTab === "products" ? "bg-blue-600 text-white" : "hover:bg-gray-100 dark:hover:bg-gray-700"}`}
            >
              <FaBoxOpen /> Products
            </button>

            <button
              onClick={() => setActiveTab("orders")}
              className={`flex items-center gap-3 w-full p-3 rounded ${activeTab === "orders" ? "bg-blue-600 text-white" : "hover:bg-gray-100 dark:hover:bg-gray-700"}`}
            >
              <FaShoppingCart /> Orders
            </button>

            <button
              onClick={() => setActiveTab("users")}
              className={`flex items-center gap-3 w-full p-3 rounded ${activeTab === "users" ? "bg-blue-600 text-white" : "hover:bg-gray-100 dark:hover:bg-gray-700"}`}
            >
              <FaUsers /> Users
            </button>
          </nav>

          <div className="mt-8">
            <div className="text-sm text-gray-500 dark:text-gray-400">Quick stats</div>
            <div className="mt-3 grid grid-cols-1 gap-3">
              <div className="p-3 bg-gray-50 dark:bg-gray-700 rounded">
                <div className="text-xs text-gray-500">Products</div>
                <div className="font-semibold text-lg">{products.length}</div>
              </div>
              <div className="p-3 bg-gray-50 dark:bg-gray-700 rounded">
                <div className="text-xs text-gray-500">Pages</div>
                <div className="font-semibold text-lg">{totalPages}</div>
              </div>
            </div>
          </div>
        </aside>

        {/* MAIN */}
        <main className="flex-1 p-6">
          {/* Header */}
          <div className="flex items-center justify-between mb-6">
            <h2 className="text-2xl font-bold">
              {activeTab === "products" && (editingId ? "Edit Product" : "Manage Products")}
              {activeTab === "orders" && "Orders & Analytics"}
              {activeTab === "users" && "Users"}
            </h2>

            <div className="flex items-center gap-3">
              <input
                value={search}
                onChange={(e) => { setSearch(e.target.value); setPage(1); }}
                placeholder="Search products..."
                className="px-3 py-2 rounded border dark:bg-gray-800 dark:border-gray-700"
              />
              <select value={pageSize} onChange={e => { setPageSize(Number(e.target.value)); setPage(1); }}
                      className="px-3 py-2 rounded border dark:bg-gray-800 dark:border-gray-700">
                <option value={4}>4 / page</option>
                <option value={6}>6 / page</option>
                <option value={9}>9 / page</option>
              </select>
            </div>
          </div>

          {/* PRODUCTS TAB */}
          {activeTab === "products" && (
            <>
              {/* FORM */}
              <form onSubmit={submit} className="bg-white dark:bg-gray-800 p-6 rounded shadow mb-6 grid grid-cols-1 md:grid-cols-3 gap-4">
                <input
                  required
                  placeholder="Product name"
                  value={form.name || ""}
                  onChange={e => setForm({...form, name: e.target.value})}
                  className="p-3 border rounded dark:bg-gray-900 dark:border-gray-700 col-span-2"
                />
                <input
                  placeholder="Price"
                  type="number"
                  value={form.price || ""}
                  onChange={e => setForm({...form, price: e.target.value})}
                  className="p-3 border rounded dark:bg-gray-900 dark:border-gray-700"
                />

                <textarea
                  placeholder="Description"
                  value={form.description || ""}
                  onChange={e => setForm({...form, description: e.target.value})}
                  className="p-3 border rounded dark:bg-gray-900 dark:border-gray-700 md:col-span-2"
                />

                <input
                  placeholder="Colors (comma separated)"
                  value={form.colors || ""}
                  onChange={e => setForm({...form, colors: e.target.value})}
                  className="p-3 border rounded dark:bg-gray-900 dark:border-gray-700"
                />

                <input
                  placeholder="Stock Count"
                  type="number"
                  value={form.countInStock || ""}
                  onChange={e => setForm({...form, countInStock: e.target.value})}
                  className="p-3 border rounded dark:bg-gray-900 dark:border-gray-700"
                />

                <div className="flex items-center gap-3">
                  <input type="file" onChange={e => setFile(e.target.files[0])} />
                  {editingId && <button type="button" onClick={() => { setEditingId(null); setForm({}); setFile(null); }} className="px-3 py-2 bg-gray-200 rounded dark:bg-gray-700">Cancel Edit</button>}
                </div>

                <div className="md:col-span-3 flex gap-3">
                  <button className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700">
                    {editingId ? "Save Changes" : "Add Product"}
                  </button>
                  {editingId && (
                    <button type="button" onClick={() => { setEditingId(null); setForm({}); setFile(null); }} className="px-4 py-2 border rounded">Clear</button>
                  )}
                </div>
              </form>

              {/* PRODUCTS GRID */}
              <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                {loading ? (
                  <div className="col-span-3 text-center py-10">Loading...</div>
                ) : paginated.length === 0 ? (
                  <div className="col-span-3 text-center py-10">No products found</div>
                ) : (
                  paginated.map(p => (
                    <div key={p._id} className="bg-white dark:bg-gray-800 p-4 rounded shadow flex flex-col">
                      <div className="h-44 overflow-hidden rounded">
                        <img src={p.image || "https://via.placeholder.com/400"} alt={p.name} className="w-full h-full object-cover" />
                      </div>
                      <div className="mt-3 flex-1">
                        <h3 className="font-semibold">{p.name}</h3>
                        <div className="text-sm text-gray-500 dark:text-gray-400">${p.price}</div>
                        <div className="text-sm mt-2 text-gray-600 dark:text-gray-300">{p.description}</div>
                      </div>

                      <div className="mt-4 flex gap-2">
                        <button onClick={() => startEdit(p)} className="flex items-center gap-2 px-3 py-2 rounded border hover:bg-gray-50 dark:hover:bg-gray-700">
                          <FaEdit /> Edit
                        </button>
                        <button onClick={() => remove(p._id)} className="flex items-center gap-2 px-3 py-2 rounded bg-red-600 text-white hover:bg-red-700">
                          <FaTrash /> Delete
                        </button>
                      </div>
                    </div>
                )))}
              </div>

              {/* PAGINATION */}
              <div className="mt-6 flex items-center justify-between">
                <div className="text-sm text-gray-600 dark:text-gray-400">
                  Showing {(page - 1) * pageSize + 1} - {Math.min(page * pageSize, filtered.length)} of {filtered.length}
                </div>

                <div className="flex items-center gap-3">
                  <button onClick={() => setPage(p => Math.max(1, p - 1))} disabled={page === 1} className="px-3 py-1 border rounded disabled:opacity-50">Prev</button>
                  <div>Page {page} / {totalPages}</div>
                  <button onClick={() => setPage(p => Math.min(totalPages, p + 1))} disabled={page === totalPages} className="px-3 py-1 border rounded disabled:opacity-50">Next</button>
                </div>
              </div>
            </>
          )}

          {/* ORDERS / CHART TAB */}
          {activeTab === "orders" && (
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div className="bg-white dark:bg-gray-800 p-4 rounded shadow">
                <h3 className="font-semibold mb-3">Monthly Sales</h3>
                <div style={{ width: "100%", height: 300 }}>
                  <ResponsiveContainer>
                    <LineChart data={chartData}>
                      <CartesianGrid strokeDasharray="3 3" />
                      <XAxis dataKey="month" />
                      <YAxis />
                      <Tooltip />
                      <Legend />
                      <Line type="monotone" dataKey="sales" stroke="#8884d8" strokeWidth={2} />
                      <Line type="monotone" dataKey="orders" stroke="#82ca9d" strokeWidth={2} />
                    </LineChart>
                  </ResponsiveContainer>
                </div>
              </div>

              <div className="bg-white dark:bg-gray-800 p-4 rounded shadow">
                <h3 className="font-semibold mb-3">Sales vs Orders</h3>
                <div style={{ width: "100%", height: 300 }}>
                  <ResponsiveContainer>
                    <BarChart data={chartData}>
                      <CartesianGrid strokeDasharray="3 3" />
                      <XAxis dataKey="month" />
                      <YAxis />
                      <Tooltip />
                      <Legend />
                      <Bar dataKey="sales" name="Sales" barSize={20} />
                      <Bar dataKey="orders" name="Orders" fill="#82ca9d" />
                    </BarChart>
                  </ResponsiveContainer>
                </div>
              </div>
            </div>
          )}

          {/* USERS TAB */}
          {activeTab === "users" && (
            <div className="bg-white dark:bg-gray-800 p-6 rounded shadow">
              <div className="text-gray-500">Users view placeholder (fetch `/users` to populate list)</div>
              {/* implement users table similarly */}
            </div>
          )}
        </main>
      </div>
    </div>
  );
}
