import { createContext, useState } from "react";
import { toast } from "react-toastify";

export const CartContext = createContext();

export const CartProvider = ({ children }) => {
  const [cart, setCart] = useState([]);

  // ⭐ Add to cart
  const addToCart = (product, qty = 1) => {
    setCart(prev => {
      const mongoId = product._id || product.id;  // ⭐ normalize ID

      const exists = prev.find(item => item._id === mongoId);

      if (exists) {
        toast.success("Quantity updated!");
        return prev.map(item =>
          item._id === mongoId
            ? { ...item, qty: item.qty + qty }
            : item
        );
      }

      toast.success("Added to cart!");
      return [...prev, { ...product, _id: mongoId, qty }];
    });
  };

  // ⭐ Remove
  const removeFromCart = (id) => {
    setCart(prev => prev.filter(item => item._id !== id));
    toast.info("Item removed.");
  };

  // ⭐ Update qty
  const updateQty = (id, newQty) => {
    if (newQty < 1) return;

    setCart(prev =>
      prev.map(item =>
        item._id === id ? { ...item, qty: newQty } : item
      )
    );
  };

  return (
    <CartContext.Provider
      value={{ cart, addToCart, removeFromCart, updateQty }}
    >
      {children}
    </CartContext.Provider>
  );
};
