import React from "react";
import Slider from "react-slick";
import ProductCard from "./ProductCard";
import Footer from "./Footer";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import "./HomePage.css";

// Banner Images
const bannerImages = [
  "https://picsum.photos/1200/500?random=51",
  "https://picsum.photos/1200/500?random=52",
  "https://picsum.photos/1200/500?random=53"
];

// Small Product Slider Data
const sliderProducts = Array.from({ length: 4 }).map((_, i) => ({
  _id: `p${i + 1}`,
  name: `Wooden Craft ${i + 1}`,
  price: (40 + i * 15).toFixed(2),
  image: `https://picsum.photos/300?random=${i + 10}`,
}));

export default function HomePage() {

  const bannerSliderSettings = {
    dots: true,
    infinite: true,
    autoplay: true,
    autoplaySpeed: 3500,
    speed: 600,
    slidesToShow: 1,
    slidesToScroll: 1
  };

  const productSliderSettings = {
    dots: false,
    arrows: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    responsive: [
      { breakpoint: 1024, settings: { slidesToShow: 2 } },
      { breakpoint: 600, settings: { slidesToShow: 1 } }
    ]
  };

  return (
    <div className="homepage-container">

      {/* üî• BANNER SLIDER */}
      <section className="hero-slider">
        <Slider {...bannerSliderSettings}>
          {bannerImages.map((img, i) => (
            <div key={i} className="banner-slide">
              <img src={img} alt={`banner-${i}`} />
            </div>
          ))}
        </Slider>
      </section>

      {/* ‚≠ê FEATURED SINGLE PRODUCT DISPLAY */}
      <section className="highlight-product">
        <div className="highlight-image">
          <img src="https://picsum.photos/600/600?random=101" alt="Featured Product" />
        </div>

        <div className="highlight-text">
          <h2>Elegant Handcrafted Wooden Lamp</h2>
          <p>
            This premium wooden lamp is carved with precision using natural timber.
            Perfect for homes, offices, and gifting ‚Äì bringing warmth and vintage charm.
          </p>
          <button className="shop-now-btn">View Product</button>
        </div>
      </section>

      {/* üéØ SMALL PRODUCT SLIDER */}
      <section className="product-section">
        <h2>Top Picks For You</h2>
        <Slider {...productSliderSettings}>
          {sliderProducts.map((product) => (
            <div key={product._id}>
              <ProductCard product={product} />
            </div>
          ))}
        </Slider>
      </section>

      {/* ‚ö° FOOTER */}
      <Footer />

    </div>
  );
}
