import React, { useState, useEffect } from "react";
import API from "../../api/api";
import { FaUsers, FaBoxOpen, FaShoppingCart, FaEdit, FaTrash } from "react-icons/fa";

export default function AdminPanel() {
  const [products, setProducts] = useState([]);
  const [users, setUsers] = useState([]);
  const [orders, setOrders] = useState([]);
  const [form, setForm] = useState({});
  const [file, setFile] = useState(null);
  const [editingId, setEditingId] = useState(null);
  const [activeTab, setActiveTab] = useState("products");

  // Load data on page load
  useEffect(() => {
    loadProducts();
    loadUsers();
    loadOrders();
  }, []);

  // Fetch Data
  const loadProducts = () =>
    API.get("/products").then(r => setProducts(r.data.products || r.data)).catch(console.error);

  const loadUsers = () =>
    API.get("/admin/users").then(r => setUsers(r.data)).catch(console.error);

  const loadOrders = () =>
    API.get("/admin/orders").then(r => setOrders(r.data)).catch(console.error);

  // Submit or Update Product
  async function submit(e) {
    e.preventDefault();
    const fd = new FormData();
    Object.entries(form).forEach(([key, val]) => fd.append(key, val || ""));
    if (file) fd.append("image", file);

    try {
      if (editingId) {
        await API.put(`/admin/product/${editingId}`, fd);
        alert("Product Updated");
      } else {
        await API.post("/admin/product", fd);
        alert("Product Added");
      }

      setForm({});
      setFile(null);
      setEditingId(null);
      loadProducts();
    } catch (err) {
      alert(err.response?.data?.message || err.message);
    }
  }

  // Delete Product
  async function remove(id) {
    if (!window.confirm("Delete product permanently?")) return;
    await API.delete(`/admin/product/${id}`);
    loadProducts();
  }

  // Edit Product Prefill
  function editProduct(p) {
    setForm(p);
    setEditingId(p._id);
  }

  // Update Order Status
  async function updateOrderStatus(id, status) {
    await API.put(`/admin/order/${id}`, { status });
    loadOrders();
    alert("Order status updated");
  }

  return (
    <div className="flex min-h-screen bg-gray-100">

      {/* Sidebar */}
      <aside className="w-64 bg-gray-900 text-white p-6 space-y-6">
        <h1 className="text-2xl font-bold">Admin Dashboard</h1>

        <nav className="space-y-4">
          <button className={`w-full text-left p-3 rounded-lg flex items-center gap-2 ${activeTab === 'products' ? 'bg-blue-600' : 'hover:bg-gray-700'}`} onClick={() => setActiveTab("products")}>
            <FaBoxOpen /> Products
          </button>

          <button className={`w-full text-left p-3 rounded-lg flex items-center gap-2 ${activeTab === 'users' ? 'bg-blue-600' : 'hover:bg-gray-700'}`} onClick={() => setActiveTab("users")}>
            <FaUsers /> Users
          </button>

          <button className={`w-full text-left p-3 rounded-lg flex items-center gap-2 ${activeTab === 'orders' ? 'bg-blue-600' : 'hover:bg-gray-700'}`} onClick={() => setActiveTab("orders")}>
            <FaShoppingCart /> Orders
          </button>
        </nav>
      </aside>

      {/* Main Section */}
      <main className="flex-1 p-6">

        {/* Header */}
        <h2 className="text-2xl font-bold mb-6">
          {activeTab === "products" && "Manage Products"}
          {activeTab === "users" && "Users List"}
          {activeTab === "orders" && "Order Management"}
        </h2>

        {/* ðŸ“¦ Product Section */}
        {activeTab === "products" && (
          <>
            <form onSubmit={submit} className="bg-white p-6 rounded-xl grid grid-cols-2 gap-4 mb-6 shadow">
              <input placeholder="Name" className="border p-3 rounded"
                value={form.name || ""} onChange={(e) => setForm({ ...form, name: e.target.value })} />

              <input placeholder="Price" type="number" className="border p-3 rounded"
                value={form.price || ""} onChange={(e) => setForm({ ...form, price: e.target.value })} />

              <input placeholder="Stock" type="number" className="border p-3 rounded"
                value={form.countInStock || ""} onChange={(e) => setForm({ ...form, countInStock: e.target.value })} />

              <textarea placeholder="Description" className="border p-3 rounded col-span-2"
                value={form.description || ""} onChange={(e) => setForm({ ...form, description: e.target.value })} />

              <input type="file" className="col-span-2" onChange={(e) => setFile(e.target.files[0])} />

              <button className="col-span-2 p-3 rounded text-white bg-blue-600 hover:bg-blue-700">
                {editingId ? "Update Product" : "Add Product"}
              </button>
            </form>

            {/* Product List */}
            <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-5">
              {products.map((p) => (
                <div key={p._id} className="bg-white p-4 rounded-xl shadow flex flex-col gap-2">
                  <img src={p.image} className="rounded-md h-40 object-cover" alt="" />
                  <h3 className="font-bold">{p.name}</h3>
                  <p className="text-gray-500">â‚¹{p.price}</p>

                  <div className="flex gap-2 mt-auto">
                    <button className="bg-green-600 text-white p-2 rounded flex-1 flex justify-center gap-2"
                      onClick={() => editProduct(p)}>
                      <FaEdit /> Edit
                    </button>
                    <button className="bg-red-600 text-white p-2 rounded flex-1 flex justify-center gap-2"
                      onClick={() => remove(p._id)}>
                      <FaTrash /> Delete
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </>
        )}

        {/* ðŸ‘¤ Users Section */}
        {activeTab === "users" && (
          <table className="w-full bg-white rounded-xl shadow">
            <thead className="bg-gray-200">
              <tr>
                <th className="p-3 text-left">Name</th>
                <th className="p-3 text-left">Email</th>
              </tr>
            </thead>
            <tbody>
              {users.map(u => (
                <tr key={u._id} className="border-b">
                  <td className="p-3">{u.name}</td>
                  <td className="p-3">{u.email}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}

        {/* ðŸ“¦ Orders Section */}
        {activeTab === "orders" && (
          <table className="w-full bg-white rounded-xl shadow">
            <thead className="bg-gray-200">
              <tr>
                <th className="p-3 text-left">User</th>
                <th className="p-3 text-left">Items</th>
                <th className="p-3 text-left">Status</th>
                <th className="p-3 text-left">Update</th>
              </tr>
            </thead>
            <tbody>
              {orders.map(o => (
                <tr key={o._id} className="border-b">
                  <td className="p-3">{o.user?.name}</td>
                  <td className="p-3">{o.items?.length} items</td>
                  <td className="p-3 font-semibold">{o.status}</td>
                  <td className="p-3">
                    <select className="border p-2" defaultValue={o.status}
                      onChange={(e) => updateOrderStatus(o._id, e.target.value)}>
                      <option>Pending</option>
                      <option>Processing</option>
                      <option>Shipped</option>
                      <option>Delivered</option>
                      <option>Cancelled</option>
                    </select>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}

      </main>
    </div>
  );
}
