import React, { useContext } from "react";
import { CartContext } from "../../contexts/CartContext";
import { useNavigate } from "react-router-dom";
import API from "../../api/api";
import "./Payments.css";

export default function SelectPayment() {
    const { cart, clearCart } = useContext(CartContext);
    const navigate = useNavigate();

    const shippingDetails =
        JSON.parse(sessionStorage.getItem("shippingDetails")) || {};

    const total = cart.reduce((sum, item) => sum + item.price * item.qty, 0);

    const generateOrderId = () =>
        "ORD-" + Math.floor(100000 + Math.random() * 900000);

    const handlePayment = async (method) => {
        const token = localStorage.getItem("token");

        if (!token) {
            alert("Please login to continue!");
            return;
        }

        const orderId = generateOrderId();

        try {
            await API.post(
                "/orders",
                {
                    orderId,
                    items: cart,
                    totalPrice: total,
                    paymentMethod: method,
                    shippingAddress: shippingDetails,
                    status: "Pending",
                },
                {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                }
            );

            clearCart();

            navigate("/order-success", {
                state: { orderId },
            });

        } catch (err) {
            console.log(err);
            alert(err.response?.data?.message || "Order failed!");
        }
    };

    return (
        <div className="payment-page">
            <h2>Select Payment Method</h2>

            <div className="payment-options">

                <div className="payment-card" onClick={() => handlePayment("COD")}>
                    <h3>Cash on Delivery</h3>
                    <p>Pay when the product arrives.</p>
                </div>

                <div className="payment-card" onClick={() => handlePayment("UPI")}>
                    <h3>UPI</h3>
                    <p>Google Pay / PhonePe / Paytm</p>
                </div>

                <div className="payment-card" onClick={() => handlePayment("Card")}>
                    <h3>Debit / Credit Card</h3>
                    <p>Visa, MasterCard, Rupay</p>
                </div>
            </div>

            <h4>Total: â‚¹{total.toFixed(2)}</h4>
        </div>
    );
}
