import React, { useContext, useState } from 'react';
import { CartContext } from '../../contexts/CartContext';
import { useNavigate } from 'react-router-dom';
import API from '../../api/api';
import '../';

export default function Checkout() {
    const { cart, clearCart } = useContext(CartContext);
    const navigate = useNavigate();

    const [form, setForm] = useState({
        fullName: '',
        email: '',
        address: '',
        city: '',
        zip: '',
        country: ''
    });

    const total = cart.reduce((sum, i) => sum + i.price * i.qty, 0);

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!cart.length) return alert('Cart is empty');
        try {
            await API.post('/orders', {
                items: cart,
                shippingAddress: form,
                totalPrice: total
            });
            alert('Order placed successfully!');
            clearCart();
            navigate('/orders');
        } catch (err) {
            alert(err.response?.data?.message || err.message);
        }
    };

    return (
        <div className="checkout-page">
            <h2>Checkout</h2>
            <div className="checkout-container">
                <div className="checkout-summary">
                    <h3>Order Summary</h3>
                    {cart.map(item => (
                        <div key={item.product} className="summary-item">
                            <span>{item.name} x {item.qty}</span>
                            <span>${(item.price * item.qty).toFixed(2)}</span>
                        </div>
                    ))}
                    <div className="summary-total"><strong>Total: ${total.toFixed(2)}</strong></div>
                </div>

                <div className="checkout-form-container">
                    <h3>Shipping Information</h3>
                    <form onSubmit={handleSubmit} className="checkout-form">
                        <input type="text" placeholder="Full Name" value={form.fullName} onChange={e => setForm({ ...form, fullName: e.target.value })} required />
                        <input type="email" placeholder="Email" value={form.email} onChange={e => setForm({ ...form, email: e.target.value })} required />
                        <input type="text" placeholder="Address" value={form.address} onChange={e => setForm({ ...form, address: e.target.value })} required />
                        <input type="text" placeholder="City" value={form.city} onChange={e => setForm({ ...form, city: e.target.value })} required />
                        <input type="text" placeholder="ZIP / Postal Code" value={form.zip} onChange={e => setForm({ ...form, zip: e.target.value })} required />
                        <input type="text" placeholder="Country" value={form.country} onChange={e => setForm({ ...form, country: e.target.value })} required />
                        <button type="submit" className="confirm-btn">Place Order</button>
                    </form>
                </div>
            </div>
        </div>
    );
}
