import React from 'react';
import { Routes, Route, Link, Navigate } from 'react-router-dom';
import HomePage from './components/HomePage';
import ProductList from './components/ProductList';
import LoginPage from "./components/Auth/Login";
import Signup from './components/Auth/Signup';
import { CartProvider } from "./contexts/CartContext";
import Cart from './components/Cart/Cart';
import Checkout from './components/Cart/Checkout';
import AdminPanel from './components/Admin/AdminPanel';
import { useAuth } from './contexts/AuthContext';
import Dashboard from './components/Admin/Dashboard';
import Products from './components/Admin/Products';
import Orders from './components/Admin/Orders';
import Services from './components/Services';
import AboutUs from './components/AboutUS';
import Settings from './components/Admin/Setting';
import './app.css';
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import logo from "./Images/Logo.jpg";

export default function App() {
  const { user, logout } = useAuth();
  const isLoggedIn = !!user;

  const handleLogout = () => {
    logout();
    toast.success("Logout Successful!");
  };

  // ðŸ”¥ USER PROTECTED ROUTE (only logged in)
  const PrivateRoute = ({ children }) => {
    return isLoggedIn ? children : <Navigate to="/login" replace />;
  };

  // ðŸ”¥ ADMIN PROTECTED ROUTE
  const AdminRoute = ({ children }) => {
    return user?.role === "admin" ? children : <Navigate to="/" replace />;
  };

  return (
    <CartProvider>
      <div>

        {/* --- TOP NAVBAR --- */}
        <nav className="navbar">
          <div className="nav-left">
            <Link className="logo" to="/">
              <img src={logo} alt="GuruKrupa Logo" className="h-10" />
            </Link>

            <div className="nav-links">
              <Link to="/">Home</Link>
              <Link to="/products">Products</Link>
              <Link to="/services">Services</Link>
              <Link to="/about">About Us</Link>

              {/* Only Admin Can See This */}
              {user?.role === "admin" && (
                <Link to="/admin/dashboard">Admin</Link>
              )}
            </div>
          </div>

          <div className="nav-center">
            <input className="search-box" placeholder="Search products..." />
          </div>

          <div className="nav-right">
            <Link className="cart-link" to="/cart">
              ðŸ›’ <span className="cart-badge"></span>
            </Link>

            <div className="auth-links">
              {isLoggedIn ? (
                <>
                  <span className="user-info">
                    Welcome, {user?.name || user?.email}
                  </span>
                  <button onClick={handleLogout} className="logout-btn">
                    Logout
                  </button>
                </>
              ) : (
                <>
                  <Link to="/login">Login</Link>
                  <Link to="/signup" className="signup-btn">Signup</Link>
                </>
              )}
            </div>
          </div>
        </nav>

        <ToastContainer position="top-right" autoClose={2000} />

        {/* --- ROUTES --- */}


      </div>
    </CartProvider>
  );
}
