// components/Admin/Orders.js
import React, { useState, useEffect } from 'react';
import API from '../../api/api';
import './Order.css';

export default function Orders() {
    const [orders, setOrders] = useState([]);

    useEffect(() => {
        loadOrders();
    }, []);

    const loadOrders = async () => {
        try {
            const res = await API.get('/admin/orders'); // backend endpoint
            setOrders(res.data.orders || res.data);
        } catch (err) {
            console.error(err);
        }
    };

    return (
        <div className="admin-orders-page">
            <h2>All Orders</h2>
            <div className="orders-table">
                <div className="orders-header">
                    <span>User</span>
                    <span>Products</span>
                    <span>Total</span>
                    <span>Status</span>
                </div>
                {orders.map(o => (
                    <div key={o._id} className="order-row">
                        <span>{o.shippingAddress.name}</span>
                        <span>{o.items.map(p => `${p.name} x ${p.qty}`).join(', ')}</span>
                        <span>${o.totalPrice.toFixed(2)}</span>
                        <span>{o.status || 'Pending'}</span>
                    </div>
                ))}
            </div>
        </div>
    );
}
