// components/Cart/Checkout.js
import React, { useContext, useState } from 'react';
import { CartContext } from '../../contexts/CartContext';
import { useNavigate } from 'react-router-dom';
import API from '../../api/api';
import './Checkouts.css';

export default function Checkout() {
    const { cart, clearCart } = useContext(CartContext);
    const navigate = useNavigate();
    const [form, setForm] = useState({
        name: '',
        email: '',
        address: '',
        city: '',
        postalCode: '',
        phone: '',
    });

    const total = cart.reduce((sum, i) => sum + i.price * i.qty, 0);

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!form.name || !form.email || !form.address) {
            return alert('Please fill in all required fields!');
        }

        const token = localStorage.getItem('token'); // get token from localStorage
        if (!token) {
            return alert('Please login to place an order!');
        }

        try {
            await API.post(
                '/orders',
                {
                    items: cart,
                    totalPrice: total,
                    shippingAddress: form,
                },
                {
                    headers: {
                        Authorization: `Bearer ${token}`, // send token in headers
                    },
                }
            );
            alert('Order placed successfully!');
            clearCart();
            navigate('/');
        } catch (err) {
            alert(err.response?.data?.message || err.message);
        }
    };

    return (
        <div className="checkout-page">
            <h2>Checkout</h2>
            <div className="checkout-container">
                <div className="checkout-form">
                    <h3>Shipping Details</h3>
                    <form onSubmit={handleSubmit}>
                        <input type="text" placeholder="Full Name" value={form.name} onChange={e => setForm({ ...form, name: e.target.value })} required />
                        <input type="email" placeholder="Email" value={form.email} onChange={e => setForm({ ...form, email: e.target.value })} required />
                        <input type="text" placeholder="Address" value={form.address} onChange={e => setForm({ ...form, address: e.target.value })} required />
                        <input type="text" placeholder="City" value={form.city} onChange={e => setForm({ ...form, city: e.target.value })} />
                        <input type="text" placeholder="Postal Code" value={form.postalCode} onChange={e => setForm({ ...form, postalCode: e.target.value })} />
                        <input type="text" placeholder="Phone Number" value={form.phone} onChange={e => setForm({ ...form, phone: e.target.value })} />
                        <button type="submit" className="place-order-btn">Place Order (${total.toFixed(2)})</button>
                    </form>
                </div>

                <div className="checkout-summary">
                    <h3>Order Summary</h3>
                    {cart.map(item => (
                        <div key={item.product} className="checkout-item">
                            <span>{item.name} x {item.qty}</span>
                            <span>${(item.price * item.qty).toFixed(2)}</span>
                        </div>
                    ))}
                    <h4>Total: ${total.toFixed(2)}</h4>
                </div>
            </div>
        </div>
    );
}
