import React, { useContext } from "react";
import { AuthProvider } from "../contexts/AuthContext";
import ProductCard from "./ProductCard";
import Footer from "./Footer";
import "./HomePage.css";
// import { useContext } from "react";
// import { AuthContext } from "../context/AuthContext";
// import { useAuth } from "../contexts/AuthContext";

function HomePages() {
  const auth = AuthProvider();

  if (!auth) {
    return <h2>Auth Context Not Found</h2>;
  }

  const { user, isLoggedIn } = auth;

  return (
    <div>
      <h1>Welcome {isLoggedIn ? user?.name : "Guest"}</h1>
    </div>
  );
}


const Profile = () => {
  const { user } = useContext(AuthProvider);

  return <h2>Logged in user: {user?.name}</h2>;
};



const featuredProduct = {
  _id: "f1",
  name: "Handcrafted Wooden Lamp",
  price: "79.99",
  image: "",
  description: "Handcrafted wooden lamp perfect for home and gifting."
};

const otherProducts = Array.from({ length: 4 }).map((_, i) => ({
  _id: `p${i + 1}`,
  name: `Wooden Decor ${i + 1}`,
  price: (40 + i * 10).toFixed(2),
  image: `https://picsum.photos/300?random=${i + 10}`,
}));

export default function HomePage() {
  const { user, logout } = useContext(AuthProvider);

  return (
    <div className="homepage-container">

      {/* üî• Navbar with Login condition */}
      <header className="navbar">
        <h2>üõç Wood Store</h2>

        {user ? (
          <button className="logout-btn" onClick={logout}>Logout</button>
        ) : (
          <button className="login-btn">Login</button>
        )}
      </header>

      {/* üî• Banner */}
      <section className="hero-banner">
        <div className="hero-text">
          <h1>Premium Wooden Gift Articles</h1>
          <p>Designed with excellence & crafted with love.</p>
          <button className="shop-now-btn">Shop Now</button>
        </div>
      </section>

      {/* ‚≠ê Featured Product */}
      <section className="highlight-product">
        <div className="highlight-image">
          <img src={featuredProduct.image} alt="Featured Product" />
        </div>

        <div className="highlight-text">
          <h2>{featuredProduct.name}</h2>
          <p>{featuredProduct.description}</p>
          <div className="price">‚Çπ {featuredProduct.price}</div>
          <button className="shop-now-btn">View Details</button>
        </div>
      </section>

      {/* üõç Other Products */}
      <section className="product-section">
        <h2>Top Picks For You</h2>
        <div className="product-grid">
          {otherProducts.map((product) => (
            <ProductCard key={product._id} product={product} />
          ))}
        </div>
      </section>

      <Footer />
    </div>
  );
  // export default Profile;
}
// export default HomePage;
