import React, { useEffect, useState } from "react";
import API from "../../api/api";
import "./Admin"; // Optional styling

const Dashboard = () => {
    const [products, setProducts] = useState([]);
    const [users, setUsers] = useState([]);
    const [orders, setOrders] = useState([]);
    const [totalSales, setTotalSales] = useState(0);

    useEffect(() => {
        fetchProducts();
        fetchUsers();
        fetchOrders();
    }, []);

    const fetchProducts = async () => {
        try {
            const res = await API.get("/products");

            // FIX: Ensure correct array extraction
            const productArr = Array.isArray(res.data)
                ? res.data
                : res.data.products || [];

            setProducts(productArr);
        } catch (error) {
            console.log("Product Fetch Error:", error);
        }
    };

    const fetchUsers = async () => {
        try {
            const res = await API.get("/users");

            const userArr = Array.isArray(res.data)
                ? res.data
                : res.data.users || [];

            setUsers(userArr);
        } catch (error) {
            console.log("User Fetch Error:", error);
        }
    };

    const fetchOrders = async () => {
        try {
            const res = await API.get("/orders");

            const orderArr = Array.isArray(res.data)
                ? res.data
                : res.data.orders || [];

            setOrders(orderArr);

            // Calculate total sales safely
            const sales = orderArr.reduce(
                (sum, order) => sum + Number(order.totalAmount || 0),
                0
            );

            setTotalSales(sales);
        } catch (error) {
            console.log("Order Fetch Error:", error);
        }
    };

    return (
        <div className="dashboard">
            <button onClick={() => window.history.back()} className="back-btn">
                â¬… Back
            </button>

            <h2>ðŸ“Š Dashboard Overview</h2>

            {/* Summary Cards */}
            <div className="stats">
                <div className="card">Products <h3>{products.length}</h3></div>
                <div className="card">Users <h3>{users.length}</h3></div>
                <div className="card">Orders <h3>{orders.length}</h3></div>
                <div className="card">Total Sales <h3>â‚¹ {totalSales}</h3></div>
            </div>

            {/* Products Table */}
            <h3>ðŸ“¦ All Products</h3>
            <table className="table">
                <thead>
                    <tr>
                        <th>Image</th><th>Name</th><th>Price</th><th>Stock</th><th>Color</th>
                    </tr>
                </thead>
                <tbody>
                    {products.length > 0 ? (
                        products.map((p) => (
                            <tr key={p._id}>
                                <td><img src={p.image} alt="" width="50" /></td>
                                <td>{p.name}</td>
                                <td>â‚¹ {p.price}</td>
                                <td>{p.stock}</td>
                                <td>{p.color}</td>
                            </tr>
                        ))
                    ) : (
                        <tr><td colSpan="5">No Products Found</td></tr>
                    )}
                </tbody>
            </table>

            {/* Users Table */}
            <h3>ðŸ‘¤ All Users</h3>
            <table className="table">
                <thead>
                    <tr><th>Name</th><th>Email</th><th>Role</th></tr>
                </thead>
                <tbody>
                    {users.length > 0 ? (
                        users.map((u) => (
                            <tr key={u._id}>
                                <td>{u.name}</td>
                                <td>{u.email}</td>
                                <td>{u.role}</td>
                            </tr>
                        ))
                    ) : (
                        <tr><td colSpan="3">No Users Found</td></tr>
                    )}
                </tbody>
            </table>

            {/* Orders Table */}
            <h3>ðŸ“„ All Orders</h3>
            <table className="table">
                <thead>
                    <tr><th>Order ID</th><th>User</th><th>Total</th><th>Status</th></tr>
                </thead>
                <tbody>
                    {orders.length > 0 ? (
                        orders.map((o) => (
                            <tr key={o._id}>
                                <td>{o._id.slice(-6)}</td>
                                <td>{o.user?.name || "Unknown"}</td>
                                <td>â‚¹ {o.totalAmount}</td>
                                <td>{o.status}</td>
                            </tr>
                        ))
                    ) : (
                        <tr><td colSpan="4">No Orders Found</td></tr>
                    )}
                </tbody>
            </table>
        </div>
    );
};

export default Dashboard;
