import React, { useState, useEffect } from 'react';
import API from '../../api/api';
import './Product.css';

export default function Products() {
    const [products, setProducts] = useState([]);
    const [form, setForm] = useState({
        name: '',
        price: '',
        colors: '',
        countInStock: ''
    });
    const [file, setFile] = useState(null);
    const [editingId, setEditingId] = useState(null);

    useEffect(() => {
        loadProducts();
    }, []);

    // LOAD PRODUCTS
    const loadProducts = async () => {
        try {
            const res = await API.get('/admin/products');
            setProducts(res.data.products || res.data);
        } catch (err) {
            console.error("LOAD PRODUCTS ERROR:", err.response?.data || err);
        }
    };

    // ADD / UPDATE PRODUCT
    const submitProduct = async (e) => {
        e.preventDefault();

        // PRICE VALIDATION
        if (Number(form.price) > 10000) {
            alert("❌ Price cannot be more than ₹10,000");
            return;
        }

        const fd = new FormData();
        fd.append('name', form.name);
        fd.append('price', form.price);
        fd.append('colors', form.colors);
        fd.append('countInStock', form.countInStock);
        if (file) fd.append('image', file);

        try {
            if (editingId) {
                await API.put(`/admin/product/${editingId}`, fd);
                setEditingId(null);
            } else {
                await API.post('/admin/product', fd);
            }

            setForm({
                name: '',
                price: '',
                colors: '',
                countInStock: ''
            });
            setFile(null);
            loadProducts();
            alert("✅ Product saved successfully!");
        } catch (err) {
            console.error("SAVE ERROR:", err.response?.data || err);
            alert(err.response?.data?.message || "Something went wrong");
        }
    };

    // EDIT PRODUCT
    const editProduct = (p) => {
        setForm({
            name: p.name || '',
            price: p.price || '',
            colors: p.colors || '',
            countInStock: p.countInStock || ''
        });
        setEditingId(p._id);
    };

    // DELETE PRODUCT
    const deleteProduct = async (id) => {
        if (!window.confirm("Are you sure you want to delete this product?")) return;

        try {
            await API.delete(`/admin/product/${id}`);
            loadProducts();
        } catch (err) {
            console.error("DELETE ERROR:", err.response?.data || err);
            alert(err.response?.data?.message || "Delete failed!");
        }
    };

    return (
        <div className="products-container">
            <h2 className="page-title">
                {editingId ? "Edit Product" : "Add New Product"}
            </h2>

            {/* PRODUCT FORM */}
            <form className="product-form" onSubmit={submitProduct}>
                <div className="form-row">
                    <input
                        type="text"
                        placeholder="Product Name"
                        value={form.name}
                        onChange={(e) => setForm({ ...form, name: e.target.value })}
                        required
                    />

                    <input
                        type="number"
                        placeholder="Price (Max ₹10,000)"
                        value={form.price}
                        min="0"
                        max="100000"
                        onChange={(e) => {
                            const value = Number(e.target.value);
                            if (value <= 10000) {
                                setForm({ ...form, price: value });
                            }
                        }}
                        required
                    />
                </div>

                <div className="form-row">
                    <input
                        type="text"
                        placeholder="Colors (comma separated)"
                        value={form.colors}
                        onChange={(e) => setForm({ ...form, colors: e.target.value })}
                    />

                    <input
                        type="number"
                        placeholder="Stock"
                        min="0"
                        value={form.countInStock}
                        onChange={(e) => setForm({ ...form, countInStock: e.target.value })}
                    />
                </div>

                <div className="form-row">
                    <input
                        type="file"
                        accept="image/*"
                        onChange={(e) => setFile(e.target.files[0])}
                    />
                </div>

                <button className="btn-submit" type="submit">
                    {editingId ? "Update Product" : "Add Product"}
                </button>
            </form>

            {/* PRODUCT LIST */}
            <h2 className="page-title">Products List</h2>

            <div className="products-grid">
                {products.length === 0 && <p>No products found.</p>}

                {products.map((p) => (
                    <div className="product-card" key={p._id}>
                        <img
                            src={p.image || 'https://via.placeholder.com/150'}
                            alt={p.name}
                            className="product-img"
                        />

                        <div className="product-info">
                            <h3>{p.name}</h3>
                            <p>Price: <strong>₹{p.price}</strong></p>
                            <p>Stock: {p.countInStock}</p>
                            <p>Colors: {p.colors}</p>
                        </div>

                        <div className="product-actions">
                            <button
                                className="btn-edit"
                                onClick={() => editProduct(p)}
                            >
                                Edit
                            </button>

                            <button
                                className="btn-delete"
                                onClick={() => deleteProduct(p._id)}
                            >
                                Delete
                            </button>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}
