import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import API from "../../api/api";
import "./AdminPanel.css";

export default function Dashboard() {
    const navigate = useNavigate();

    const [stats, setStats] = useState({
        totalProducts: 0,
        totalUsers: 0,
        totalOrders: 0,
        totalSales: 0,
    });

    const [products, setProducts] = useState([]);
    const [users, setUsers] = useState([]);
    const [orders, setOrders] = useState([]);

    useEffect(() => {
        loadDashboardData();
    }, []);

    async function loadDashboardData() {
        try {
            const productRes = await API.get("/products");
            const usersRes = await API.get("/users");
            const ordersRes = await API.get("/orders");

            const orderData = ordersRes.data || [];

            setStats({
                totalProducts: productRes.data.products?.length || productRes.data.length,
                totalUsers: usersRes.data.length,
                totalOrders: orderData.length,
                totalSales: orderData.reduce((sum, o) => sum + (o.totalAmount || 0), 0),
            });

            setProducts(productRes.data.products || productRes.data);
            setUsers(usersRes.data);
            setOrders(orderData);

        } catch (err) {
            console.error(err);
        }
    }

    return (
        <div className="dashboard">
            <button className="back-btn" onClick={() => navigate("/admin")}>â¬… Back</button>

            <h1 className="dashboard-title">Dashboard Overview</h1>

            {/* ----------- INFO CARDS ----------- */}
            <div className="cards">
                <div className="card"><h3>Total Products</h3><p>{stats.totalProducts}</p></div>
                <div className="card"><h3>Total Users</h3><p>{stats.totalUsers}</p></div>
                <div className="card"><h3>Total Orders</h3><p>{stats.totalOrders}</p></div>
                <div className="card"><h3>Total Sales</h3><p>â‚¹ {stats.totalSales}</p></div>
            </div>

            {/* ----------- ALL PRODUCTS ----------- */}
            <h2>ðŸ“¦ All Products</h2>
            <table className="recent-table">
                <thead>
                    <tr>
                        <th>Image</th>
                        <th>Name</th>
                        <th>Price (â‚¹)</th>
                        <th>Stock</th>
                        <th>Colors</th>
                    </tr>
                </thead>
                <tbody>
                    {products.map(p => (
                        <tr key={p._id}>
                            <td><img src={p.image} alt="" width="50" /></td>
                            <td>{p.name}</td>
                            <td>{p.price}</td>
                            <td>{p.countInStock}</td>
                            <td>{p.colors}</td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {/* ----------- ALL USERS ----------- */}
            <h2>ðŸ‘¤ All Users</h2>
            <table className="recent-table">
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Role</th>
                    </tr>
                </thead>
                <tbody>
                    {users.map(u => (
                        <tr key={u._id}>
                            <td>{u.name}</td>
                            <td>{u.email}</td>
                            <td>{u.isAdmin ? "Admin" : "User"}</td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {/* ----------- ALL ORDERS ----------- */}
            <h2>ðŸ§¾ All Orders</h2>
            <table className="recent-table">
                <thead>
                    <tr>
                        <th>Order ID</th>
                        <th>User</th>
                        <th>Total (â‚¹)</th>
                        <th>Status</th>
                        <th>Date</th>
                    </tr>
                </thead>

                <tbody>
                    {orders.map((order) => (
                        <tr key={order._id}>
                            <td>{order._id.slice(-6)}</td>
                            <td>{order.user?.name || "Unknown"}</td>
                            <td>â‚¹ {order.totalAmount}</td>
                            <td><span className={`status ${order.status}`}>{order.status}</span></td>
                            <td>{new Date(order.createdAt).toLocaleDateString()}</td>
                        </tr>
                    ))}
                </tbody>
            </table>

        </div>
    );
}
