import React, { useState, useEffect } from 'react';
import { NavLink } from 'react-router-dom';
import API from '../../api/api';
import './AdminPanel.css';
import da from './Dashboard'
export default function AdminPanel() {
  const [form, setForm] = useState({});
  const [file, setFile] = useState(null);
  const [products, setProducts] = useState([]);
  const [editingId, setEditingId] = useState(null); // for update

  useEffect(() => {
    load();
  }, []);

  function load() {
    API.get('/products')
      .then(r => setProducts(r.data.products || r.data))
      .catch(console.error);
  }

  async function submit(e) {
    e.preventDefault();
    const fd = new FormData();
    fd.append('name', form.name || '');
    fd.append('description', form.description || '');
    fd.append('price', form.price || 0);
    fd.append('colors', form.colors || '');
    fd.append('countInStock', form.countInStock || 0);
    if (file) fd.append('image', file);

    try {
      if (editingId) {
        await API.put('/admin/product/' + editingId, fd, {
          headers: { 'Content-Type': 'multipart/form-data' },
        });
        setEditingId(null);
      } else {
        await API.post('/admin/product', fd, {
          headers: { 'Content-Type': 'multipart/form-data' },
        });
      }
      alert('Saved');
      setForm({});
      setFile(null);
      load();
    } catch (err) {
      alert(err.response?.data?.message || err.message);
    }
  }

  async function remove(id) {
    if (!window.confirm('Are you sure you want to delete this product?')) return;
    try {
      await API.delete('/admin/product/' + id);
      load();
    } catch (e) {
      alert(e.message);
    }
  }

  function editProduct(p) {
    setForm({
      name: p.name,
      description: p.description,
      price: p.price,
      colors: p.colors,
      countInStock: p.countInStock,
    });
    setEditingId(p._id);
  }

  return (
    <div className="admin-container">
      {/* Sidebar */}
      <div className="sidebar">
        <h2>Admin Panel</h2>
        <NavLink to="/dashborad">Dashboard</NavLink>
        <NavLink to="/products">Products</NavLink>
        <NavLink to="/orders">Orders</NavLink>
        <NavLink to="/users">Users</NavLink>
        <NavLink to="/settings">Settings</NavLink>
      </div>

      {/* Main Content */}
      <div className="main-content">
        <div className="header">
          <h1>Products Management</h1>
          <div className="user">Admin</div>
        </div>

        {/* Form */}
        <form onSubmit={submit} className="admin-form">
          <input
            placeholder="Name"
            onChange={e => setForm({ ...form, name: e.target.value })}
            value={form.name || ''}
          />
          <input
            placeholder="Price"
            onChange={e => setForm({ ...form, price: e.target.value })}
            value={form.price || ''}
          />
          <input
            placeholder="Colors (comma)"
            onChange={e => setForm({ ...form, colors: e.target.value })}
            value={form.colors || ''}
          />
          <input
            placeholder="Stock"
            onChange={e => setForm({ ...form, countInStock: e.target.value })}
            value={form.countInStock || ''}
          />
          <input type="file" onChange={e => setFile(e.target.files[0])} />
          <button>{editingId ? 'Update Product' : 'Add Product'}</button>
        </form>

        {/* Products List */}
        <div className="admin-list">
          {products.map(p => (
            <div key={p._id} className="admin-card">
              <img src={p.image || 'https://via.placeholder.com/150'} alt={p.name} />
              <div>{p.name}</div>
              <div className="price">${p.price}</div>
              <div className="btns">
                <button className="update-btn" onClick={() => editProduct(p)}>Update</button>
                <button className="delete-btn" onClick={() => remove(p._id)}>Delete</button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
