// App.js
import React, { useContext } from "react";
import { Routes, Route, Link, Navigate } from "react-router-dom";

import HomePage from "./components/HomePage";
import ProductList from "./components/ProductList";
import LoginPage from "./components/Auth/Login";
import Signup from "./components/Auth/Signup";

import { CartProvider } from "./contexts/CartContext";
import { CartContext } from "./contexts/CartContext";
import { useAuth } from "./contexts/AuthContext";

import Cart from "./components/Cart/Cart";
import Checkout from "./components/Cart/Checkout";
import Payment from "./components/Cart/payment";

import AdminPanel from "./components/Admin/AdminPanel";
import Dashboard from "./components/Admin/Dashboard";
import Products from "./components/Admin/Products";
import Orders from "./components/Admin/Orders";
import Settings from "./components/Admin/Setting";

import "./app.css";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

import logo from "./Images/Logo.jpg";
import AdminOrders from "./components/Admin/AdminOrders";
import UserOrders from "./components/Orders/UserOrders";
import OrderSuccess from "./components/Cart/OrderSuccess";



export default function App() {
  const { user, logout } = useAuth();
  const isLoggedIn = !!user;

  // Cart items from context
  const { cart } = useContext(CartContext);

  // Count cart items
  const totalItems = cart.reduce((sum, item) => sum + item.quantity, 0);

  const handleLogout = () => {
    logout();
    toast.success("Logout Successful!");
  };

  return (
    <CartProvider>
      <div>

        {/* ==================== NAVBAR ==================== */}
        <nav className="navbar">
          <div className="nav-left">
            <Link className="logo" to="/">
              <img src={logo} alt="GuruKrupa Logo" className="h-10" />
            </Link>

            <div className="nav-links">
              <Link to="/">Home</Link>
              <Link to="/products">Products</Link>

              {/* ‚≠ê SHOW ADMIN LINK WHEN USER IS ADMIN */}
              {user?.role?.toLowerCase() === "admin" && (
                <Link to="/admin" className="admin-link">
                  Admin
                </Link>
              )}
            </div>
          </div>

          <div className="nav-center">
            <input className="search-box" placeholder="Search products..." />
          </div>

          <div className="nav-right">
            {/* ‚≠ê Cart Icon */}
            <Link className="cart-link" to="/cart">
              üõí
              {totalItems > 0 && (
                <span className="cart-badge">{totalItems}</span>
              )}
            </Link>

            <div className="auth-links">
              {isLoggedIn ? (
                <>
                  <span className="user-info">
                    Welcome, {user?.name || user?.email}
                  </span>
                  <button onClick={handleLogout} className="logout-btn">
                    Logout
                  </button>
                </>
              ) : (
                <>
                  <Link to="/login">Login</Link>
                  <Link to="/signup" className="signup-btn">Signup</Link>
                </>
              )}
            </div>
          </div>
        </nav>

        <ToastContainer position="top-right" autoClose={2000} />

        {/* ==================== ROUTES ==================== */}
        <div className="page-container">
          <Routes>
            {/* Public */}
            <Route path="/" element={<HomePage />} />
            <Route path="/products" element={<ProductList />} />

            {/* Auth */}
            <Route
              path="/login"
              element={isLoggedIn ? <Navigate to="/" /> : <LoginPage />}
            />
            <Route
              path="/signup"
              element={isLoggedIn ? <Navigate to="/" /> : <Signup />}
            />

            {/* Cart */}
            <Route path="/cart" element={<Cart />} />
            <Route
              path="/checkout"
              element={isLoggedIn ? <Checkout /> : <Navigate to="/login" />}
            />
            <Route path="/payment" element={<Payment />} />

            {/* ADMIN PROTECTED ROUTES */}
            <Route
              path="/admin"
              element={
                user?.role?.toLowerCase() === "admin" ? (
                  <AdminPanel />
                ) : (
                  <Navigate to="/" />
                )
              }
            >
              <Route path="dashboard" element={<Dashboard />} />
              <Route path="products" element={<Products />} />
              <Route path="orders" element={<Orders />} />
              <Route path="settings" element={<Settings />} />
            </Route>
          </Routes>
        </div>

      </div>
    </CartProvider>
  );
}
