// components/Admin/Orders.js
import React, { useState, useEffect } from 'react';
import API from '../../api/api';
import './Order.css';

export default function Orders() {
    const [orders, setOrders] = useState([]);

    useEffect(() => {
        loadOrders();
    }, []);

    const loadOrders = async () => {
        try {
            const res = await API.get('/admin/orders', {
                headers: {
                    Authorization: `Bearer ${localStorage.getItem("token")}`
                }
            });

            console.log("ORDERS RESPONSE:", res.data);

            setOrders(res.data.orders || res.data);
        } catch (err) {
            console.log("ORDER LOAD ERROR:", err.response?.data || err);
            alert(err.response?.data?.message || "Cannot load orders");
        }
    };

    return (
        <div className="admin-orders-page">
            <h2>All Orders</h2>

            {orders.length === 0 ? (
                <p className="no-orders">No orders found</p>
            ) : (
                <div className="orders-table">
                    <div className="orders-header">
                        <span>User</span>
                        <span>Products</span>
                        <span>Total</span>
                        <span>Status</span>
                    </div>

                    {orders.map(o => (
                        <div key={o._id} className="order-row">
                            <span>{o?.shippingAddress?.name || "Unknown"}</span>
                            <span>
                                {o?.items?.map(p => `${p.name} x ${p.qty}`).join(", ")}
                            </span>
                            <span>â‚¹{o.totalPrice?.toFixed(2)}</span>
                            <span>{o.status || "Pending"}</span>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
}
