import React, { useState, useEffect } from 'react';
import API from '../../api/api';

export default function Orders() {
    const [orders, setOrders] = useState([]);

    useEffect(() => {
        loadOrders();
    }, []);

    const loadOrders = async () => {
        try {
            const res = await API.get('/admin/orders'); // backend endpoint
            setOrders(res.data.orders || res.data);
        } catch (err) {
            console.error(err);
        }
    };

    return (
        <div>
            <h2>Orders</h2>
            <table>
                <thead>
                    <tr>
                        <th>User</th>
                        <th>Products</th>
                        <th>Total</th>
                        <th>Status</th>
                    </tr>
                </thead>
                <tbody>
                    {orders.map(o => (
                        <tr key={o._id}>
                            <td>{o.userName}</td>
                            <td>{o.products.map(p => p.name).join(', ')}</td>
                            <td>${o.total}</td>
                            <td>{o.status}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}
