import React, { useContext } from 'react';
import { CartContext } from '../../contexts/CartContext';
import API from '../../api/api';
import { useNavigate } from 'react-router-dom';
import './Cart.css';

export default function Cart() {
  const { cart, removeFromCart, clearCart, updateQty } = useContext(CartContext);
  const navigate = useNavigate();
  const total = cart.reduce((sum, i) => sum + i.price * i.qty, 0);

  async function checkout() {
    const token = localStorage.getItem('token');
    if (!token) return alert('Please login to checkout');
    try {
      await API.post('/orders', {
        items: cart,
        shippingAddress: 'Default address',
        totalPrice: total,
      });
      alert('Order placed successfully!');
      clearCart();
      navigate('/orders');
    } catch (err) {
      alert(err.response?.data?.message || err.message);
    }
  }

  if (cart.length === 0)
    return (
      <div className="cart-page empty-cart">
        <h2>Your Cart is Empty</h2>
        <p>Looks like you havenâ€™t added anything yet.</p>
        <button className="continue-btn" onClick={() => navigate('/products')}>
          Continue Shopping
        </button>
      </div>
    );

  return (
    <div className="cart-page">
      <h2>Your Cart</h2>
      {cart.map((item) => (
        <div key={item.product} className="cart-item">
          <img src={item.image || 'https://via.placeholder.com/80'} alt={item.name} />
          <div className="item-details">
            <div className="item-name">{item.name}</div>
            <div className="item-price">${item.price.toFixed(2)}</div>
            <div className="item-quantity">
              Quantity:
              <button onClick={() => updateQty(item.product, item.qty - 1)} disabled={item.qty === 1}>
                -
              </button>
              <span>{item.qty}</span>
              <button onClick={() => updateQty(item.product, item.qty + 1)}>+</button>
            </div>
            <div className="item-subtotal">Subtotal: ${(item.price * item.qty).toFixed(2)}</div>
          </div>
          <button className="remove-btn" onClick={() => removeFromCart(item.product)}>
            Remove
          </button>
        </div>
      ))}

      <div className="cart-summary">
        <h3>Total: ${total.toFixed(2)}</h3>
        <div className="buttons">
          <button className="checkout-btn" onClick={checkout}>
            Checkout
          </button>
          <button className="continue-btn" onClick={() => navigate('/products')}>
            Continue Shopping
          </button>
        </div>
      </div>
    </div>
  );
}
