import React, { useContext } from 'react';
import { CartContext } from '../../contexts/CartContext';
import { useNavigate } from 'react-router-dom';
import './Cart.css';

export default function Cart() {
  const { cart, removeFromCart, updateQty } = useContext(CartContext); // must match provider
  const navigate = useNavigate();
  const total = cart.reduce((sum, i) => sum + i.price * i.qty, 0);

  if (cart.length === 0)
    return (
      <div className="cart-page empty-cart">
        <h2>Your Cart is Empty</h2>
        <button onClick={() => navigate('/products')}>Continue Shopping</button>
      </div>
    );

  return (
    <div className="cart-page">
      <h2>Your Shopping Cart</h2>
      {cart.map(item => (
        <div key={item.product} className="cart-card">
          <img src={item.image || 'https://via.placeholder.com/100'} alt={item.name} />
          <div className="cart-info">
            <h3>{item.name}</h3>
            <p>${item.price.toFixed(2)}</p>
            <div className="qty-control">
              <button onClick={() => updateQty(item.product, item.qty - 1)} disabled={item.qty === 1}>-</button>
              <span>{item.qty}</span>
              <button onClick={() => updateQty(item.product, item.qty + 1)}>+</button>
            </div>
            <p>Subtotal: ${(item.price * item.qty).toFixed(2)}</p>
          </div>
          <button onClick={() => removeFromCart(item.product)}>Remove</button>
        </div>
      ))}
      <div className="cart-summary">
        <h3>Total: ${total.toFixed(2)}</h3>
        <button onClick={() => navigate('/checkout')}>Checkout</button>
      </div>
    </div>
  );
}
