import React, { useState, useEffect } from "react";
import API from "../../api/api";
import { FaUsers, FaBoxOpen, FaShoppingCart, FaTrash } from "react-icons/fa";
import { useAuth } from '../../contexts/AuthContext'

export default function AdminPanel() {
  const [form, setForm] = useState({});
  const [file, setFile] = useState(null);
  const [products, setProducts] = useState([]);
  const [activeTab, setActiveTab] = useState("products");

  useEffect(() => {
    load();
  }, []);

  function load() {
    API.get("/products")
      .then((r) => setProducts(r.data.products || r.data))
      .catch(console.error);
  }

  async function submit(e) {
    e.preventDefault();
    const fd = new FormData();
    fd.append("name", form.name || "");
    fd.append("description", form.description || "");
    fd.append("price", form.price || 0);
    fd.append("colors", form.colors || "");
    fd.append("countInStock", form.countInStock || 0);
    if (file) fd.append("image", file);

    try {
      await API.post("/admin/product", fd, {
        headers: { "Content-Type": "multipart/form-data" },
      });
      alert("Product Added Successfully");
      setForm({});
      setFile(null);
      load();
    } catch (err) {
      alert(err.response?.data?.message || err.message);
    }
  }

  async function remove(id) {
    if (!window.confirm("Delete product permanently?")) return;
    try {
      await API.delete("/admin/product/" + id);
      load();
    } catch (e) {
      alert(e.message);
    }
  }

  return (
    <div className="flex min-h-screen bg-gray-100">
      {/* Sidebar */}
      <aside className="w-64 bg-gray-900 text-white p-6 space-y-6">
        <h1 className="text-2xl font-bold">Admin Dashboard</h1>

        <nav className="space-y-4">
          <button className={`flex items-center gap-3 p-3 rounded-lg w-full text-left ${activeTab === 'products' ? 'bg-blue-600' : 'hover:bg-gray-700'}`}
            onClick={() => setActiveTab("products")}>
            <FaBoxOpen /> Products
          </button>
          <button className={`flex items-center gap-3 p-3 rounded-lg w-full text-left ${activeTab === 'users' ? 'bg-blue-600' : 'hover:bg-gray-700'}`}
            onClick={() => setActiveTab("users")}>
            <FaUsers /> Users
          </button>
          <button className={`flex items-center gap-3 p-3 rounded-lg w-full text-left ${activeTab === 'orders' ? 'bg-blue-600' : 'hover:bg-gray-700'}`}
            onClick={() => setActiveTab("orders")}>
            <FaShoppingCart /> Orders
          </button>
        </nav>
      </aside>

      {/* Main Page */}
      <main className="flex-1 p-6">

        {/* Header */}
        <div className="bg-white p-4 rounded-xl shadow-md mb-6 flex justify-between items-center">
          <h2 className="text-xl font-bold">
            {activeTab === "products" && "Manage Products"}
            {activeTab === "users" && "All Users"}
            {activeTab === "orders" && "Order Management"}
          </h2>
          <div className="flex items-center gap-4">
            <div className="text-gray-600">Admin</div>
            <button onClick={logout} className="bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700">Logout</button>
          </div>
        </div>

        {/* Products View */}
        {activeTab === "products" && (
          <>
            {/* Form */}
            <form
              onSubmit={submit}
              className="bg-white p-6 rounded-xl shadow-md grid grid-cols-2 gap-4 mb-8"
            >
              <input
                placeholder="Product Name"
                className="border p-3 rounded"
                onChange={(e) => setForm({ ...form, name: e.target.value })}
                value={form.name || ""}
              />

              <input
                placeholder="Price"
                type="number"
                className="border p-3 rounded"
                onChange={(e) => setForm({ ...form, price: e.target.value })}
                value={form.price || ""}
              />

              <input
                placeholder="Colors (comma separated)"
                className="border p-3 rounded"
                onChange={(e) => setForm({ ...form, colors: e.target.value })}
                value={form.colors || ""}
              />

              <input
                placeholder="Stock Count"
                type="number"
                className="border p-3 rounded"
                onChange={(e) =>
                  setForm({ ...form, countInStock: e.target.value })
                }
                value={form.countInStock || ""}
              />

              <input
                type="file"
                className="border p-3 rounded col-span-2"
                onChange={(e) => setFile(e.target.files[0])}
              />

              <button className="bg-blue-600 text-white p-3 rounded col-span-2 hover:bg-blue-700">
                Add Product
              </button>
            </form>

            {/* Product Cards */}
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              {products.map((p) => (
                <div
                  key={p._id}
                  className="bg-white p-4 rounded-xl shadow-md flex flex-col"
                >
                  <img
                    src={p.image || "https://via.placeholder.com/150"}
                    alt=""
                    className="rounded-lg h-40 w-full object-cover"
                  />
                  <h3 className="font-semibold mt-3">{p.name}</h3>
                  <p className="text-gray-600">${p.price}</p>

                  <button
                    onClick={() => remove(p._id)}
                    className="mt-auto bg-red-600 text-white p-2 rounded hover:bg-red-700 flex items-center justify-center gap-2"
                  >
                    <FaTrash /> Delete
                  </button>
                </div>
              ))}
            </div>
          </>
        )}

        {/* Future Tabs Placeholder */}
        {activeTab !== "products" && (
          <div className="text-center text-gray-500 mt-20 text-xl">
            This section will be added soon ðŸš§
          </div>
        )}
      </main>
    </div>
  );
}
