import React, { useState, useEffect } from 'react';
import API from '../../api/api';

export default function Products() {
    const [products, setProducts] = useState([]);
    const [form, setForm] = useState({});
    const [file, setFile] = useState(null);
    const [editingId, setEditingId] = useState(null);

    useEffect(() => { loadProducts(); }, []);

    const loadProducts = async () => {
        try {
            const res = await API.get('/products');
            setProducts(res.data.products || res.data);
        } catch (err) { console.error(err); }
    };

    const submitProduct = async (e) => {
        e.preventDefault();
        const fd = new FormData();
        fd.append('name', form.name || '');
        fd.append('price', form.price || 0);
        fd.append('colors', form.colors || '');
        fd.append('countInStock', form.countInStock || 0);
        if (file) fd.append('image', file);

        try {
            if (editingId) {
                await API.put(`/admin/product/${editingId}`, fd);
                setEditingId(null);
            } else {
                await API.post('/admin/product', fd);
            }
            setForm({}); setFile(null); loadProducts();
            alert('Saved!');
        } catch (err) { alert(err.message); }
    };

    const editProduct = (p) => setForm(p) || setEditingId(p._id);
    const deleteProduct = async (id) => { if (window.confirm('Delete?')) { await API.delete(`/admin/product/${id}`); loadProducts(); } };

    return (
        <div>
            <h2>Products</h2>
            <form onSubmit={submitProduct}>
                <input placeholder="Name" value={form.name || ''} onChange={e => setForm({ ...form, name: e.target.value })} />
                <input placeholder="Price" value={form.price || ''} onChange={e => setForm({ ...form, price: e.target.value })} />
                <input placeholder="Colors" value={form.colors || ''} onChange={e => setForm({ ...form, colors: e.target.value })} />
                <input placeholder="Stock" value={form.countInStock || ''} onChange={e => setForm({ ...form, countInStock: e.target.value })} />
                <input type="file" onChange={e => setFile(e.target.files[0])} />
                <button>{editingId ? 'Update' : 'Add'} Product</button>
            </form>

            <div className="products-list">
                {products.map(p => (
                    <div key={p._id}>
                        <img src={p.image || 'https://via.placeholder.com/150'} alt={p.name} />
                        <div>{p.name} - ${p.price}</div>
                        <button onClick={() => editProduct(p)}>Edit</button>
                        <button onClick={() => deleteProduct(p._id)}>Delete</button>
                    </div>
                ))}
            </div>
        </div>
    );
}
