// frontend/src/components/Admin/Dashboard.js
import React, { useEffect, useState } from "react";
import API from "../../api/api";
import "./AdminPanels.css";

export default function Dashboard() {
    const [products, setProducts] = useState([]);
    const [users, setUsers] = useState([]);
    const [orders, setOrders] = useState([]);
    const [totalSales, setTotalSales] = useState(0);

    useEffect(() => { fetchProducts(); fetchUsers(); fetchOrders(); }, []);

    const fetchProducts = async () => {
        try {
            const res = await API.get("/products");
            const arr = Array.isArray(res.data) ? res.data : (res.data.products || []);
            setProducts(arr);
        } catch (err) { console.error(err); }
    };

    const fetchUsers = async () => {
        try {
            const res = await API.get("/users");
            const arr = Array.isArray(res.data) ? res.data : (res.data.users || []);
            setUsers(arr);
        } catch (err) { console.error(err); }
    };

    const fetchOrders = async () => {
        try {
            const res = await API.get("/orders");
            const arr = Array.isArray(res.data) ? res.data : (res.data.orders || res.data);
            setOrders(arr);
            const sales = arr.reduce((s, o) => s + Number(o.totalAmount || o.totalPrice || 0), 0);
            setTotalSales(sales);
        } catch (err) { console.error(err); }
    };

    return (
        <div className="dashboard">
            <h2>ðŸ“Š Dashboard Overview</h2>
            <div className="stats">
                <div className="card">Products <h3>{products.length}</h3></div>
                <div className="card">Users <h3>{users.length}</h3></div>
                <div className="card">Orders <h3>{orders.length}</h3></div>
                <div className="card">Total Sales <h3>â‚¹ {totalSales}</h3></div>
            </div>

            {/* tables omitted for brevity â€” reuse your existing table code but use fields totalAmount/status/user */}
        </div>
    );
}
