import React, { useState, useEffect } from "react";
import API from "../../api/api";
import "./Order.css";

// ðŸ‘‡ GET BASE URL FROM AXIOS
const BASE_URL = API.defaults.baseURL.replace("/api", "");

export default function Orders() {
    const [orders, setOrders] = useState([]);
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        loadOrders();
    }, []);

    const loadOrders = async () => {
        try {
            setLoading(true);
            const res = await API.get("/orders");

            const arr = Array.isArray(res.data)
                ? res.data
                : res.data.orders || res.data.data || [];

            setOrders(arr);
        } catch (err) {
            console.error(err);
        } finally {
            setLoading(false);
        }
    };

    const updateStatus = async (orderId, newStatus) => {
        await API.put(`/orders/${orderId}/status`, { status: newStatus });
        setOrders(prev =>
            prev.map(o =>
                o._id === orderId ? { ...o, status: newStatus } : o
            )
        );
    };

    return (
        <div className="admin-orders-page">
            <h2>All Orders</h2>

            <div className="orders-table">
                <div className="orders-header">
                    <strong>User Details</strong>
                    <strong>Products</strong>
                    <strong>Total</strong>
                    <strong>Status</strong>
                </div>

                {orders.map(o => (
                    <div key={o._id} className="order-row">

                        {/* USER */}
                        <div className="order-col user-details">
                            <p><b>Name:</b> {o.user?.name}</p>
                            <p><b>Email:</b> {o.user?.email}</p>
                            <p><b>Phone:</b> {o.user?.phone}</p>
                            <p><b>Address:</b> {o.shippingAddress?.address}</p>
                        </div>

                        {/* PRODUCTS */}
                        <div className="order-col products-col">
                            {o.items.map((item, i) => (
                                <div key={i} className="product-item">
                                    <img
                                        src={
                                            item.product?.image
                                                ? `${BASE_URL}/${item.product.image}`
                                                : "/no-image.png"
                                        }
                                        alt={item.product?.name}
                                        className="product-img"
                                    />

                                    <div className="product-info">
                                        <p><b>{item.product?.name}</b></p>
                                        <p>Qty: {item.qty}</p>
                                        <p>Price: â‚¹ {item.product?.price}</p>
                                    </div>
                                </div>
                            ))}
                        </div>

                        <div className="order-col">â‚¹ {o.totalAmount}</div>

                        <div className="order-col">
                            <select
                                value={o.status}
                                onChange={(e) =>
                                    updateStatus(o._id, e.target.value)
                                }
                            >
                                <option>Pending</option>
                                <option>Accepted</option>
                                <option>Shipped</option>
                                <option>Delivered</option>
                                <option>Rejected</option>
                            </select>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}
