import React, { useContext, useState } from 'react';
import { CartContext } from '../../contexts/CartContext';
import API from '../../api/api';
import { useNavigate } from 'react-router-dom';
import './Cart.css';

export default function Cart() {
  const { cart, removeFromCart, clearCart, updateQty } = useContext(CartContext);
  const navigate = useNavigate();
  const [showCheckout, setShowCheckout] = useState(false);
  const [checkoutForm, setCheckoutForm] = useState({
    fullName: '',
    email: '',
    address: '',
    city: '',
    zip: '',
    country: '',
  });

  const total = cart.reduce((sum, i) => sum + i.price * i.qty, 0);

  async function handleCheckout(e) {
    e.preventDefault();
    if (cart.length === 0) return alert('Cart is empty');

    try {
      await API.post('/orders', {
        items: cart,
        shippingAddress: checkoutForm,
        totalPrice: total,
      });
      alert('Order placed successfully!');
      clearCart();
      setShowCheckout(false);
      navigate('/orders');
    } catch (err) {
      alert(err.response?.data?.message || err.message);
    }
  }

  if (cart.length === 0)
    return (
      <div className="cart-page empty-cart">
        <h2>Your Cart is Empty</h2>
        <p>Looks like you havenâ€™t added anything yet.</p>
        <button className="continue-btn" onClick={() => navigate('/products')}>
          Continue Shopping
        </button>
      </div>
    );

  return (
    <div className="cart-page">
      <h2>Your Cart</h2>

      {cart.map((item) => (
        <div key={item.product} className="cart-item">
          <img src={item.image || 'https://via.placeholder.com/80'} alt={item.name} />
          <div className="item-details">
            <div className="item-name">{item.name}</div>
            <div className="item-price">${item.price.toFixed(2)}</div>
            <div className="item-quantity">
              Quantity:
              <button onClick={() => updateQty(item.product, item.qty - 1)} disabled={item.qty === 1}>-</button>
              <span>{item.qty}</span>
              <button onClick={() => updateQty(item.product, item.qty + 1)}>+</button>
            </div>
            <div className="item-subtotal">Subtotal: ${(item.price * item.qty).toFixed(2)}</div>
          </div>
          <button className="remove-btn" onClick={() => removeFromCart(item.product)}>Remove</button>
        </div>
      ))}

      <div className="cart-summary">
        <h3>Total: ${total.toFixed(2)}</h3>
        <div className="buttons">
          <button className="checkout-btn" onClick={() => setShowCheckout(true)}>
            Checkout
          </button>
          <button className="continue-btn" onClick={() => navigate('/products')}>
            Continue Shopping
          </button>
        </div>
      </div>

      {/* Dynamic Checkout Form */}
      {showCheckout && (
        <div className="checkout-form-container">
          <h3>Checkout Form</h3>
          <form onSubmit={handleCheckout} className="checkout-form">
            <input
              type="text"
              placeholder="Full Name"
              value={checkoutForm.fullName}
              onChange={(e) => setCheckoutForm({ ...checkoutForm, fullName: e.target.value })}
              required
            />
            <input
              type="email"
              placeholder="Email"
              value={checkoutForm.email}
              onChange={(e) => setCheckoutForm({ ...checkoutForm, email: e.target.value })}
              required
            />
            <input
              type="text"
              placeholder="Address"
              value={checkoutForm.address}
              onChange={(e) => setCheckoutForm({ ...checkoutForm, address: e.target.value })}
              required
            />
            <input
              type="text"
              placeholder="City"
              value={checkoutForm.city}
              onChange={(e) => setCheckoutForm({ ...checkoutForm, city: e.target.value })}
              required
            />
            <input
              type="text"
              placeholder="ZIP / Postal Code"
              value={checkoutForm.zip}
              onChange={(e) => setCheckoutForm({ ...checkoutForm, zip: e.target.value })}
              required
            />
            <input
              type="text"
              placeholder="Country"
              value={checkoutForm.country}
              onChange={(e) => setCheckoutForm({ ...checkoutForm, country: e.target.value })}
              required
            />

            <div className="checkout-buttons">
              <button type="submit" className="confirm-btn">Confirm Order</button>
              <button type="button" className="cancel-btn" onClick={() => setShowCheckout(false)}>Cancel</button>
            </div>
          </form>
        </div>
      )}
    </div>
  );
}
