import React from 'react';
import { Routes, Route, Link, Navigate } from 'react-router-dom';
import HomePage from './components/HomePage';
import ProductList from './components/ProductList';
import LoginPage from "./components/Auth/LoginPage"; // fixed import
import Signup from './components/Auth/Signup';
import { CartProvider } from "./contexts/CartContext";
import Cart from './components/Cart/Cart';
import Checkout from './components/Cart/Checkout';
import AdminPanel from './components/Admin/AdminPanel';
import ProtectedRoute from './components/ProtectedRoute';
import { useAuth } from './contexts/AuthContext';
import Dashboard from './components/Admin/Dashboard';
import Products from './components/Admin/Products';
import Orders from './components/Admin/Orders';
import Settings from './components/Admin/Setting';
import './app.css';
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

export default function App() {
  const { user, logout } = useAuth();
  const isLoggedIn = !!user;

  const handleLogout = () => {
    logout(); // clears context + localStorage
    toast.success("Logout Successful!");
  };

  return (
    <CartProvider>
      <div>

        {/* --- TOP NAVBAR --- */}
        <nav className="navbar">
          <div className="nav-left">
            <Link className="logo" to="/">GuruKrupa GiftArticle</Link>

            <div className="nav-links">
              <Link to="/">Home</Link>
              <Link to="/products">Products</Link>

              {/* üî• Admin link only for admin users */}
              {user?.role === "admin" && (
                <Link to="/admin">Admin</Link>
              )}
            </div>
          </div>

          <div className="nav-center">
            <input className="search-box" placeholder="Search products..." />
          </div>

          <div className="nav-right">
            <Link className="cart-link" to="/cart">
              üõí <span className="cart-badge"></span>
            </Link>

            <div className="auth-links">
              {isLoggedIn ? (
                <>
                  <span className="user-info">
                    Welcome, {user?.name || user?.email}
                  </span>
                  <button onClick={handleLogout} className="logout-btn">Logout</button>
                </>
              ) : (
                <>
                  <Link to="/login">Login</Link>
                  <Link to="/signup" className="signup-btn">Signup</Link>
                </>
              )}
            </div>
          </div>
        </nav>

        <ToastContainer position="top-right" autoClose={2000} />

        {/* --- ROUTES --- */}
        <div className="page-container">
          <Routes>
            <Route path="/" element={<HomePage />} />
            <Route path="/products" element={<ProductList />} />
            <Route path="/login" element={isLoggedIn ? <Navigate to="/" /> : <LoginPage />} />
            <Route path="/signup" element={isLoggedIn ? <Navigate to="/" /> : <Signup />} />
            <Route path="/cart" element={<Cart />} />

            {/* üîê Checkout only accessible if logged in */}
            <Route path="/checkout" element={
              isLoggedIn ? <Checkout /> : <Navigate to="/login" replace />
            } />

            {/* üîê Admin Only Routes */}
            <Route path="/admin" element={
              user?.role === "admin" ? <AdminPanel /> : <Navigate to="/" replace />
            }>
              <Route path="dashboard" element={<Dashboard />} />
              <Route path="products" element={<Products />} />
              <Route path="orders" element={<Orders />} />
              <Route path="settings" element={<Settings />} />
            </Route>

          </Routes>
        </div>

      </div>
    </CartProvider>
  );
}
