import React, { useContext, useState } from "react";
import { CartContext } from "../../contexts/CartContext";
import { useNavigate } from "react-router-dom";
import API from "../../api/api";
import "./Payments.css";

export default function Payment() {
    const { cart, clearCart } = useContext(CartContext);
    const navigate = useNavigate();
    const [showPopup, setShowPopup] = useState(false);

    const shipping = JSON.parse(localStorage.getItem("shippingDetails"));
    const token = localStorage.getItem("token");

    const total = cart.reduce((sum, item) => sum + item.price * item.qty, 0);

    const placeOrder = async () => {
        try {
            await API.post(
                "/orders",
                {
                    items: cart,
                    totalPrice: total,
                    shippingAddress: shipping,
                    paymentMethod: "COD",
                },
                {
                    headers: { Authorization: `Bearer ${token}` },
                }
            );

            clearCart();
            navigate("/order-success");

        } catch (err) {
            alert(err.response?.data?.message || err.message);
        }
    };

    return (
        <div className="payment-page">
            <h2>Select Payment Method</h2>

            <div className="payment-options">
                <div className="payment-card">
                    <h3>Cash on Delivery (COD)</h3>
                    <p>Pay when your order arrives at your home.</p>
                </div>
            </div>

            <button className="confirm-btn" onClick={() => setShowPopup(true)}>
                Confirm Order
            </button>

            {/* Pop-up Modal */}
            {showPopup && (
                <div className="popup-overlay">
                    <div className="popup-box">
                        <h3>Confirm Order?</h3>
                        <p>Your order will be placed and visible in Admin Panel.</p>

                        <div className="popup-actions">
                            <button className="cancel-btn" onClick={() => setShowPopup(false)}>Cancel</button>
                            <button className="place-btn" onClick={placeOrder}>Place Order</button>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
}
