import React, { useEffect, useState } from "react";
import API from "../../api/api";
import "./AdminPanels.css";

const Dashboard = () => {
    const [products, setProducts] = useState([]);
    const [users, setUsers] = useState([]);
    const [orders, setOrders] = useState([]);
    const [totalSales, setTotalSales] = useState(0);

    useEffect(() => {
        fetchProducts();
        fetchUsers();
        fetchOrders();
    }, []);

    // ---------------- FETCH PRODUCTS ----------------
    const fetchProducts = async () => {
        try {
            const res = await API.get("/api/products");
            setProducts(res.data.products || res.data || []);
        } catch (error) {
            console.log("Product Fetch Error:", error);
        }
    };

    // ---------------- FETCH USERS ----------------
    const fetchUsers = async () => {
        try {
            const res = await API.get("/api/users");  // FIXED
            setUsers(res.data.users || res.data || []);
        } catch (error) {
            console.log("User Fetch Error:", error);
        }
    };

    // ---------------- FETCH ORDERS ----------------
    const fetchOrders = async () => {
        try {
            const res = await API.get("/api/orders/all"); // FIXED
            const orderArr = res.data.orders || res.data || [];

            setOrders(orderArr);

            const total = orderArr.reduce(
                (sum, order) => sum + Number(order.totalAmount || order.totalPrice || 0),
                0
            );

            setTotalSales(total);
        } catch (error) {
            console.log("Order Fetch Error:", error);
        }
    };

    return (
        <div className="dashboard">
            <button onClick={() => window.history.back()} className="back-btn">
                â¬… Back
            </button>

            <h2>ðŸ“Š Dashboard Overview</h2>

            <div className="stats">
                <div className="card">Products <h3>{products.length}</h3></div>
                <div className="card">Users <h3>{users.length}</h3></div>
                <div className="card">Orders <h3>{orders.length}</h3></div>
                <div className="card">Total Sales <h3>â‚¹ {totalSales}</h3></div>
            </div>
        </div>
    );
};

export default Dashboard;
