import React from "react";
import { Link, useNavigate } from "react-router-dom";
import { useAuth } from "../../contexts/AuthContext";
import "./Navbar.css";

export default function Navbar() {
  const { user, logout } = useAuth(); // get user + logout from context
  const navigate = useNavigate();

  const handleLogout = () => {
    logout(); // remove user & token from context + localStorage
    navigate("/"); // redirect to home
  };

  const isLoggedIn = !!user; // true if user exists

  return (
    <nav className="navbar">
      <div className="nav-left">
        <Link className="logo" to="/">GuruKrupa GiftArticle</Link>

        <div className="nav-links">
          <Link to="/">Home</Link>
          <Link to="/products">Products</Link>

          {/* Admin link visible only for admin users */}
          {user?.role === "admin" && (
            <Link to="/admin">Admin</Link>
          )}
        </div>
      </div>

      <div className="nav-center">
        <input className="search-box" placeholder="Search products..." />
      </div>

      <div className="nav-right">
        <Link className="cart-link" to="/cart">
          ðŸ›’ <span className="cart-badge"></span>
        </Link>

        <div className="auth-links">
          {isLoggedIn ? (
            <>
              <span className="user-info">
                Welcome, {user?.name || user?.email}
              </span>
              <button onClick={handleLogout} className="logout-btn">
                Logout
              </button>
            </>
          ) : (
            <>
              <Link to="/login">Login</Link>
              <Link to="/signup" className="signup-btn">Signup</Link>
            </>
          )}
        </div>
      </div>
    </nav>
  );
}
