import React, { createContext, useState, useEffect } from 'react';
export const CartContext = createContext();
export function CartProvider({ children }){
  const [cart, setCart] = useState(()=> { try{ return JSON.parse(localStorage.getItem('cart'))||[] }catch{return []} });
  useEffect(()=> localStorage.setItem('cart', JSON.stringify(cart)), [cart]);
  const addToCart = (product, qty=1) => {
    setCart(prev=>{
      const found = prev.find(p=>p.product===product._id);
      if(found) return prev.map(p=> p.product===product._id ? {...p, qty: p.qty+qty} : p);
      return [...prev, { product: product._id, name: product.name, image: product.image, price: product.price, qty }];
    });
  };
  const removeFromCart = (id) => setCart(prev => prev.filter(p=>p.product!==id));
  const clearCart = () => setCart([]);
  return <CartContext.Provider value={{ cart, addToCart, removeFromCart, clearCart }}>{children}</CartContext.Provider>
}
