import React, { useState, useEffect } from "react";
import API from "../../api/api";
import { FaUsers, FaBoxOpen, FaShoppingCart, FaEdit, FaTrash } from "react-icons/fa";
import "./AdminPanel.css"; // <-- NEW CSS FILE

export default function AdminPanel() {
  const [products, setProducts] = useState([]);
  const [users, setUsers] = useState([]);
  const [orders, setOrders] = useState([]);
  const [form, setForm] = useState({});
  const [file, setFile] = useState(null);
  const [editingId, setEditingId] = useState(null);
  const [activeTab, setActiveTab] = useState("products");

  useEffect(() => {
    loadProducts();
    loadUsers();
    loadOrders();
  }, []);

  const loadProducts = () =>
    API.get("/products").then(r => setProducts(r.data.products || r.data)).catch(console.error);

  const loadUsers = () =>
    API.get("/admin/users").then(r => setUsers(r.data)).catch(console.error);

  const loadOrders = () =>
    API.get("/admin/orders").then(r => setOrders(r.data)).catch(console.error);

  async function submit(e) {
    e.preventDefault();
    const fd = new FormData();
    Object.entries(form).forEach(([key, val]) => fd.append(key, val || ""));
    if (file) fd.append("image", file);

    try {
      if (editingId) {
        await API.put(`/admin/product/${editingId}`, fd);
        alert("Product Updated");
      } else {
        await API.post("/admin/product", fd);
        alert("Product Added");
      }

      setForm({});
      setFile(null);
      setEditingId(null);
      loadProducts();
    } catch (err) {
      alert(err.response?.data?.message || err.message);
    }
  }

  async function remove(id) {
    if (!window.confirm("Delete product permanently?")) return;
    await API.delete(`/admin/product/${id}`);
    loadProducts();
  }

  function editProduct(p) {
    setForm(p);
    setEditingId(p._id);
  }

  async function updateOrderStatus(id, status) {
    await API.put(`/admin/order/${id}`, { status });
    loadOrders();
    alert("Order status updated");
  }

  return (
    <div className="admin-container">

      <aside className="sidebar">
        <h1>Admin Dashboard</h1>

        <button className={activeTab === "products" ? "active" : ""} onClick={() => setActiveTab("products")}>
          <FaBoxOpen /> Products
        </button>

        <button className={activeTab === "users" ? "active" : ""} onClick={() => setActiveTab("users")}>
          <FaUsers /> Users
        </button>

        <button className={activeTab === "orders" ? "active" : ""} onClick={() => setActiveTab("orders")}>
          <FaShoppingCart /> Orders
        </button>
      </aside>

      <main className="main-content">
        <h2>
          {activeTab === "products" && "Manage Products"}
          {activeTab === "users" && "Users List"}
          {activeTab === "orders" && "Order Management"}
        </h2>

        {/* PRODUCT FORM */}
        {activeTab === "products" && (
          <>
            <form className="product-form" onSubmit={submit}>
              <input placeholder="Name" value={form.name || ""} onChange={(e) => setForm({ ...form, name: e.target.value })} />
              <input placeholder="Price" type="number" value={form.price || ""} onChange={(e) => setForm({ ...form, price: e.target.value })} />
              <input placeholder="Stock" type="number" value={form.countInStock || ""} onChange={(e) => setForm({ ...form, countInStock: e.target.value })} />
              <textarea placeholder="Description" value={form.description || ""} onChange={(e) => setForm({ ...form, description: e.target.value })} />
              <input type="file" onChange={(e) => setFile(e.target.files[0])} />

              <button>{editingId ? "Update Product" : "Add Product"}</button>
            </form>

            {/* PRODUCT LIST */}
            <div className="product-grid">
              {products.map(p => (
                <div className="product-card" key={p._id}>
                  <img src={p.image} alt="" />
                  <h3>{p.name}</h3>
                  <p>â‚¹{p.price}</p>

                  <div className="actions">
                    <button className="edit" onClick={() => editProduct(p)}>
                      <FaEdit /> Edit
                    </button>
                    <button className="delete" onClick={() => remove(p._id)}>
                      <FaTrash /> Delete
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </>
        )}

        {/* USERS */}
        {activeTab === "users" && (
          <table className="data-table">
            <thead><tr><th>Name</th><th>Email</th></tr></thead>
            <tbody>
              {users.map(u => (
                <tr key={u._id}><td>{u.name}</td><td>{u.email}</td></tr>
              ))}
            </tbody>
          </table>
        )}

        {/* Orders */}
        {activeTab === "orders" && (
          <table className="data-table">
            <thead>
              <tr><th>User</th><th>Items</th><th>Status</th><th>Action</th></tr>
            </thead>
            <tbody>
              {orders.map(o => (
                <tr key={o._id}>
                  <td>{o.user?.name}</td>
                  <td>{o.items?.length} Items</td>
                  <td>{o.status}</td>
                  <td>
                    <select defaultValue={o.status} onChange={(e) => updateOrderStatus(o._id, e.target.value)}>
                      <option>Pending</option>
                      <option>Processing</option>
                      <option>Shipped</option>
                      <option>Delivered</option>
                      <option>Cancelled</option>
                    </select>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </main>
    </div>
  );
}
