import React, { useState, useContext } from "react";
import { CartContext } from "../../contexts/CartContext";
import { useNavigate } from "react-router-dom";
import API from "../../api/api";
import "./Payments.css";

export default function Payment() {
    const { cart, clearCart } = useContext(CartContext);
    const navigate = useNavigate();

    const [selectedMethod, setSelectedMethod] = useState("COD");
    const [confirmPopup, setConfirmPopup] = useState(false);
    const [successPopup, setSuccessPopup] = useState(false);

    const shippingDetails =
        JSON.parse(localStorage.getItem("shippingDetails")) || {};

    const total = cart.reduce((sum, i) => sum + i.price * i.qty, 0);

    const handleOrder = async () => {
        const token = localStorage.getItem("token");

        if (!token) return alert("You must be logged in!");

        try {
            // â­ FIXED: Proper item mapping
            const formattedItems = cart.map(item => ({
                product: item._id, // MUST BE VALID MongoDB ID
                name: item.name,
                image: item.image,
                qty: item.qty,
                price: item.price,
            }));

            await API.post(
                "/orders",
                {
                    items: formattedItems,
                    shippingAddress: {
                        name: shippingDetails.name,
                        email: shippingDetails.email,
                        address: shippingDetails.address,
                        city: shippingDetails.city,
                        postalCode: shippingDetails.postalCode,
                        phone: shippingDetails.phone,
                    },
                    totalPrice: total,
                    paymentMethod: selectedMethod,
                },
                {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                }
            );

            clearCart();
            setConfirmPopup(false);
            setSuccessPopup(true);
        } catch (err) {
            console.error(err);
            alert(err.response?.data?.message || "Something went wrong");
        }
    };

    return (
        <div className="payment-page">
            <h2>Select Payment Method</h2>

            <div className="payment-box">
                <div
                    className={`payment-option ${selectedMethod === "COD" ? "active" : ""}`}
                    onClick={() => setSelectedMethod("COD")}
                >
                    Cash on Delivery (COD)
                </div>

                <button className="continue-btn" onClick={() => setConfirmPopup(true)}>
                    Continue
                </button>
            </div>

            {confirmPopup && (
                <div className="popup-overlay">
                    <div className="popup-box">
                        <h3>Confirm Order?</h3>
                        <p>Your order will be placed and visible in Admin Panel.</p>

                        <div className="popup-actions">
                            <button className="cancel-btn" onClick={() => setConfirmPopup(false)}>
                                Cancel
                            </button>

                            <button className="place-btn" onClick={handleOrder}>
                                Place Order
                            </button>
                        </div>
                    </div>
                </div>
            )}

            {successPopup && (
                <div className="popup-overlay">
                    <div className="popup-box success">
                        <h3>ðŸŽ‰ Order Placed Successfully!</h3>
                        <p>Your order is now visible in the Admin Panel.</p>

                        <div className="popup-actions" style={{ marginTop: "25px" }}>
                            <button className="cancel-btn" onClick={() => setSuccessPopup(false)}>
                                Close
                            </button>

                            <button
                                className="place-btn"
                                onClick={() => {
                                    setSuccessPopup(false);
                                    navigate("/");
                                }}
                            >
                                Continue Shopping
                            </button>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
}
