import React, { useState, useEffect } from 'react';
import { NavLink, Outlet } from 'react-router-dom';
import API from '../../api/api';
import './AdminPanel.css';

export default function AdminPanel() {
  const [products, setProducts] = useState([]);
  const [orders, setOrders] = useState([]);
  const [form, setForm] = useState({});
  const [file, setFile] = useState(null);
  const [editingId, setEditingId] = useState(null);

  // Load products & orders
  useEffect(() => {
    loadProducts();
    loadOrders();
  }, []);

  const loadProducts = async () => {
    try {
      const res = await API.get('/products');
      setProducts(res.data.products || res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const loadOrders = async () => {
    try {
      const res = await API.get('/admin/orders');
      setOrders(res.data.orders || res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const submitProduct = async (e) => {
    e.preventDefault();
    const fd = new FormData();
    fd.append('name', form.name || '');
    fd.append('price', form.price || 0);
    fd.append('colors', form.colors || '');
    fd.append('countInStock', form.countInStock || 0);
    if (file) fd.append('image', file);

    try {
      if (editingId) {
        await API.put(`/admin/product/${editingId}`, fd, {
          headers: { 'Content-Type': 'multipart/form-data' },
        });
        setEditingId(null);
      } else {
        await API.post(`/admin/product`, fd, {
          headers: { 'Content-Type': 'multipart/form-data' },
        });
      }
      setForm({});
      setFile(null);
      loadProducts();
      alert('Saved successfully!');
    } catch (err) {
      alert(err.response?.data?.message || err.message);
    }
  };

  const editProduct = (p) => {
    setForm(p);
    setEditingId(p._id);
  };

  const deleteProduct = async (id) => {
    if (!window.confirm('Are you sure you want to delete this product?')) return;
    try {
      await API.delete(`/admin/product/${id}`);
      loadProducts();
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className="admin-container">
      {/* Sidebar */}
      <div className="sidebar">
        <h2>Admin Panel</h2>
        <NavLink to="/admin/dashboard">Dashboard</NavLink>
        <NavLink to="/admin/products">Products</NavLink>
        <NavLink to="/admin/orders">Orders</NavLink>
        <NavLink to="/admin/users">Users</NavLink>
        <NavLink to="/admin/settings">Settings</NavLink>
      </div>

      {/* Main Content */}
      <div className="main-content">
        <Outlet /> {/* For nested routes like dashboard, orders, settings */}

        {/* Products Management Section */}
        <div className="products-section">
          <h2>Products Management</h2>
          <form onSubmit={submitProduct} className="admin-form">
            <input
              placeholder="Name"
              value={form.name || ''}
              onChange={e => setForm({ ...form, name: e.target.value })}
            />
            <input
              placeholder="Price"
              value={form.price || ''}
              onChange={e => setForm({ ...form, price: e.target.value })}
            />
            <input
              placeholder="Colors (comma)"
              value={form.colors || ''}
              onChange={e => setForm({ ...form, colors: e.target.value })}
            />
            <input
              placeholder="Stock"
              value={form.countInStock || ''}
              onChange={e => setForm({ ...form, countInStock: e.target.value })}
            />
            <input type="file" onChange={e => setFile(e.target.files[0])} />
            <button>{editingId ? 'Update Product' : 'Add Product'}</button>
          </form>

          <div className="admin-list">
            {products.map(p => (
              <div key={p._id} className="admin-card">
                <img src={p.image || 'https://via.placeholder.com/150'} alt={p.name} />
                <div>{p.name}</div>
                <div className="price">${p.price}</div>
                <div className="btns">
                  <button onClick={() => editProduct(p)} className="update-btn">Update</button>
                  <button onClick={() => deleteProduct(p._id)} className="delete-btn">Delete</button>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Orders Section */}
        <div className="orders-section">
          <h2>Orders</h2>
          <table className="orders-table">
            <thead>
              <tr>
                <th>User</th>
                <th>Products</th>
                <th>Total</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {orders.map(o => (
                <tr key={o._id}>
                  <td>{o.userName}</td>
                  <td>{o.products.map(p => p.name).join(', ')}</td>
                  <td>${o.total}</td>
                  <td>{o.status}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  );
}
