import React from "react";
import Slider from "react-slick";
import ProductCard from "./ProductCard";
import Footer from "./Footer";
import "./HomePage.css";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

// Banner Images
const bannerImages = [
  "https://picsum.photos/1200/500?random=51",
  "https://picsum.photos/1200/500?random=52",
  "https://picsum.photos/1200/500?random=53"
];

// Fake Data
const featuredProducts = Array.from({ length: 5 }).map((_, i) => ({
  _id: `f${i + 1}`,
  name: `Premium Wooden Craft ${i + 1}`,
  price: (50 + i * 10).toFixed(2),
  image: `https://picsum.photos/300?random=${i + 1}`,
}));

export default function HomePage() {

  const bannerSliderSettings = {
    dots: true,
    infinite: true,
    autoplay: true,
    autoplaySpeed: 3500,
    speed: 600,
    slidesToShow: 1,
    slidesToScroll: 1
  };

  const productSliderSettings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
    responsive: [
      { breakpoint: 1024, settings: { slidesToShow: 2 } },
      { breakpoint: 600, settings: { slidesToShow: 1 } }
    ]
  };

  return (
    <div className="homepage-container">

      {/* HERO BANNER SLIDER */}
      <section className="hero-slider">
        <Slider {...bannerSliderSettings}>
          {bannerImages.map((img, i) => (
            <div key={i} className="banner-slide">
              <img src={img} alt={`banner-${i}`} />
            </div>
          ))}
        </Slider>
      </section>

      {/* FEATURED SINGLE PRODUCT SECTION */}
      <section className="highlight-product">
        <div className="highlight-image">
          <img src="https://picsum.photos/600/600?random=101" alt="Showcase" />
        </div>
        <div className="highlight-text">
          <h2>Handcrafted Wooden Lamp</h2>
          <p>
            A signature artisan-crafted wooden masterpiece that elevates home decor.
            Ideal for gifting or personal spaces.
          </p>
          <button className="shop-now-btn">View Product</button>
        </div>
      </section>

      {/* FEATURED PRODUCTS */}
      <section className="product-section">
        <h2>Our Best Crafted Pieces</h2>
        <Slider {...productSliderSettings}>
          {featuredProducts.map((p) => (
            <div key={p._id}>
              <ProductCard product={p} />
            </div>
          ))}
        </Slider>
      </section>

      <Footer />
    </div>
  );
}
