// frontend/src/components/Cart/Payment.jsx
import React, { useState, useContext, useEffect } from "react";
import { CartContext } from "../../contexts/CartContext";
import { useNavigate } from "react-router-dom";
import API from "../../api/api";
import "./Payments.css";

export default function Payment() {
    const { cart, clearCart } = useContext(CartContext);
    const navigate = useNavigate();

    const [selectedMethod, setSelectedMethod] = useState("COD");
    const [confirmPopup, setConfirmPopup] = useState(false);
    const [successPopup, setSuccessPopup] = useState(false);
    const [shippingDetails, setShippingDetails] = useState(null);

    useEffect(() => {
        const saved = JSON.parse(localStorage.getItem("shippingDetails"));
        if (!saved) navigate("/checkout");
        setShippingDetails(saved);
    }, []);

    const total = cart.reduce((s, i) => s + i.price * i.qty, 0);

    const placeOrder = async () => {
        const token = localStorage.getItem("token");
        if (!token) return alert("Please login to place order");

        try {
            const formattedItems = cart.map(item => ({
                product: item._id,
                name: item.name,
                image: item.image,
                qty: item.qty,
                price: item.price
            }));

            await API.post("/orders", {
                items: formattedItems,
                shippingAddress: shippingDetails,
                totalAmount: total,
                paymentMethod: selectedMethod
            });

            clearCart();
            setConfirmPopup(false);
            setSuccessPopup(true);
        } catch (err) {
            console.error(err);
            alert(err.response?.data?.message || "Something went wrong");
        }
    };

    return (
        <div className="payment-page">
            <h2>Select Payment Method</h2>

            <div className="payment-box">
                <div className={`payment-option ${selectedMethod === "COD" ? "active" : ""}`} onClick={() => setSelectedMethod("COD")}>
                    Cash on Delivery (COD)
                </div>
                <button className="continue-btn" onClick={() => setConfirmPopup(true)}>Continue</button>
            </div>

            {confirmPopup && (
                <div className="popup-overlay">
                    <div className="popup-box">
                        <h3>Confirm Order?</h3>
                        <p>Your order will be placed and visible in Admin Panel.</p>
                        <div className="popup-actions">
                            <button className="cancel-btn" onClick={() => setConfirmPopup(false)}>Cancel</button>
                            <button className="place-btn" onClick={placeOrder}>Place Order</button>
                        </div>
                    </div>
                </div>
            )}

            {successPopup && (
                <div className="popup-overlay">
                    <div className="popup-box success">
                        <h3>ðŸŽ‰ Order Placed Successfully!</h3>
                        <p>Your order is now visible in the Admin Panel.</p>
                        <div className="popup-actions">
                            <button onClick={() => setSuccessPopup(false)} className="cancel-btn">Close</button>
                            <button onClick={() => { setSuccessPopup(false); navigate("/"); }} className="place-btn">Continue Shopping</button>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
}
