import React, { useContext } from 'react';
import { CartContext } from '../../contexts/CartContext';
import API from '../../api/api';

export default function Cart(){
  const { cart, removeFromCart, clearCart } = useContext(CartContext);
  const total = cart.reduce((s,i)=> s + i.price * i.qty, 0);

  async function checkout(){
    const token = localStorage.getItem('token');
    if(!token) return alert('Please login to checkout');
    try{
      await API.post('/orders', { items: cart, shippingAddress: 'Default address', totalPrice: total });
      alert('Order placed');
      clearCart();
    }catch(err){ alert(err.response?.data?.message || err.message); }
  }

  return (
    <div className="cart-page">
      <h2>Your Cart</h2>
      {cart.length === 0 ? <p>Cart empty</p> : cart.map(i=>(
        <div key={i.product} className="cart-item">
          <img src={i.image || 'https://via.placeholder.com/80'} alt="" />
          <div>{i.name} x {i.qty} = ${ (i.price*i.qty).toFixed(2) }</div>
          <button onClick={()=>removeFromCart(i.product)}>Remove</button>
        </div>
      ))}
      <h3>Total: ${total.toFixed(2)}</h3>
      <button onClick={checkout}>Checkout</button>
    </div>
  );
}
